 61	    }
 62	
 63	    console.log(`Filtered config files: ${filteredConfigFiles.length} files`);
 64	
 65	    if (filteredConfigFiles.length === 0) {
 66	      return [];
 67	    }
 68	
 69	    try {
 70	      // Generate Nx configurations using sequential processing via stdin
 71	      const batchResults = await generateBatchNxConfigFromMavenAsync(filteredConfigFiles, opts, context);
 72	
 73	      console.log(`Sequential processing generated ${Object.keys(batchResults).length} project configurations`);
 74	
 75	      // Convert batch results to the expected format
 76	      const tupleResults: Array<[string, any]> = [];
