 80	        const configFile = filteredConfigFiles.find(file => dirname(file) === projectRoot);
 81	        if (!configFile) continue;
 82	
 83	        // Cache the batch results for createDependencies to use later
 84	        cachedBatchResults.set(projectRoot, nxConfig);
 85	
 86	        // Normalize and validate targets
 87	        const normalizedTargets = normalizeTargets(nxConfig.targets || {}, opts);
 88	
 89	        const result = {
 90	          projects: {
 91	            [projectRoot]: {
 92	              name: nxConfig.name || projectRoot.split('/').pop() || 'unknown',
 93	              root: projectRoot,
 94	              sourceRoot: nxConfig.sourceRoot || join(projectRoot, 'src/main/java'),
 95	              projectType: nxConfig.projectType || 'library',
 96	              targets: normalizedTargets,
 97	              tags: nxConfig.tags || [],
 98	              implicitDependencies: nxConfig.implicitDependencies,
 99	              namedInputs: nxConfig.namedInputs,
100	              metadata: {
101	                technologies: ['maven', 'java'],
102	                framework: detectFramework(nxConfig),
103	              },
104	            },
