121	/**
122	 * Create dependencies based on Maven dependency analysis
123	 */
124	// Cache for batch results to avoid re-processing in createDependencies
125	let cachedBatchResults: Map<string, any> = new Map();
126	
127	/**
128	 * Create dependencies using cached batch results (no additional processes)
129	 */
130	export const createDependencies: CreateDependencies = async () => {
131	  const dependencies: ReturnType<CreateDependencies> = [];
132	
133	  // Use cached results from createNodesV2 batch processing
134	  for (const [projectRoot, nxConfig] of cachedBatchResults.entries()) {
135	    if (!nxConfig?.implicitDependencies) continue;
136	
137	    const sourceProjectName = nxConfig.name;
138	    if (!sourceProjectName) continue;
139	
140	    // Add project dependencies
141	    const projectDeps = nxConfig.implicitDependencies.projects || [];
142	    for (const depName of projectDeps) {
143	      // Find target project in cached results
144	      for (const [_, targetConfig] of cachedBatchResults.entries()) {
145	        if (targetConfig.name === depName) {
146	          dependencies.push({
147	            source: sourceProjectName,
148	            target: targetConfig.name,
149	            type: DependencyType.static,
150	          });
151	          break;
152	        }
153	      }
154	    }
155	
156	    // Add parent dependencies
157	    const parentDeps = nxConfig.implicitDependencies.inheritsFrom || [];
158	    for (const parentName of parentDeps) {
159	      // Find parent project in cached results
160	      for (const [_, parentConfig] of cachedBatchResults.entries()) {
161	        if (parentConfig.name === parentName) {
162	          dependencies.push({
163	            source: sourceProjectName,
164	            target: parentConfig.name,
165	            type: DependencyType.implicit,
166	          });
167	          break;
168	        }
169	      }
170	    }
171	  }
172	
173	  console.log(`Generated ${dependencies.length} dependencies from cached batch results`);
174	  return dependencies;
175	};
176	
177	/**
178	 * Generate Nx configuration by calling the Java Maven analyzer (async version)
179	 */
180	async function generateNxConfigFromMavenAsync(
181	  pomPath: string,
182	  options: MavenPluginOptions,
183	  context: CreateNodesContextV2
184	): Promise<any> {
185	  const javaAnalyzerPath = findJavaAnalyzer();
186	  if (!javaAnalyzerPath) {
187	    throw new Error('Maven analyzer Java program not found. Please ensure maven-script is compiled.');
188	  }
189	
190	  try {
191	    // Use Maven exec to run the Java program with proper classpath
192	    const mavenScriptDir = javaAnalyzerPath.endsWith('.jar')
193	      ? dirname(javaAnalyzerPath)
194	      : dirname(dirname(javaAnalyzerPath)); // target/classes -> project root
195	
196	    // Convert relative path to absolute path
197	    const absolutePomPath = pomPath.startsWith('/') ? pomPath : join(workspaceRoot, pomPath);
198	    
199	    console.log(`Analyzing ${absolutePomPath} using Maven script in ${mavenScriptDir}`);
200	
201	    const command = `${options.mavenExecutable} exec:java -Dexec.mainClass="MavenModelReader" -Dexec.args="${absolutePomPath} --nx" -q`;
202	    const { stdout, stderr } = await execAsync(command, {
203	      cwd: mavenScriptDir,
204	      timeout: 30000, // 30 second timeout
205	      maxBuffer: 1024 * 1024 * 10, // 10MB buffer for large outputs
206	    });
207	
208	    if (stderr && stderr.trim()) {
209	      console.warn(`Maven analyzer stderr: ${stderr}`);
210	    }
211	
212	    // Parse JSON output from the Java program
213	    const output = stdout;
214	    const jsonStart = output.indexOf('{');
215	    const jsonEnd = output.lastIndexOf('}') + 1;
216	
217	    if (jsonStart === -1 || jsonEnd === 0) {
218	      throw new Error('No valid JSON output from Maven analyzer');
219	    }
220	
221	    const jsonOutput = output.substring(jsonStart, jsonEnd);
222	    return JSON.parse(jsonOutput);
223	
224	  } catch (error) {
225	    throw new Error(`Failed to execute Maven analyzer: ${error.message}`);
226	  }
227	}
228	
229	/**
230	 * Generate Nx configurations for multiple projects using batch processing
231	 */
232	async function generateBatchNxConfigFromMavenAsync(
233	  pomPaths: string[],
234	  options: MavenPluginOptions,
235	  context: CreateNodesContextV2
236	): Promise<Record<string, any>> {
237	  const javaAnalyzerPath = findJavaAnalyzer();
238	  if (!javaAnalyzerPath) {
239	    throw new Error('Maven analyzer Java program not found. Please ensure maven-script is compiled.');
240	  }
241	  
242	  try {
243	    // Use Maven exec to run the Java program with stdin
244	    const mavenScriptDir = javaAnalyzerPath.endsWith('.jar') 
245	      ? dirname(javaAnalyzerPath) 
246	      : dirname(dirname(javaAnalyzerPath)); // target/classes -> project root
247	    
248	    // Convert relative paths to absolute paths
249	    const absolutePomPaths = pomPaths.map(pomPath => 
250	      pomPath.startsWith('/') ? pomPath : join(workspaceRoot, pomPath)
251	    );
252	    
253	    console.log(`Batch analyzing ${absolutePomPaths.length} Maven projects using stdin in ${mavenScriptDir}`);
254	    
255	    // Use spawn to pass input via stdin
256	    const inputData = absolutePomPaths.join('\n') + '\n';
257	    
258	    // Create unique output file name to avoid conflicts
259	    const outputFile = join(workspaceRoot, `maven-results-${Date.now()}.json`);
260	    
261	    const { stdout, stderr } = await new Promise<{stdout: string, stderr: string}>((resolve, reject) => {
262	      const child = spawn(options.mavenExecutable, [
263	        'exec:java',
264	        '-Dexec.mainClass=MavenModelReader',
265	        '-Dexec.args=--stdin --nx',
266	        `-Dmaven.output.file=${outputFile}`,
267	        '-q'
268	      ], {
269	        cwd: mavenScriptDir,
270	        stdio: ['pipe', 'pipe', 'pipe']
271	      });
272	
273	      let stdout = '';
274	      let stderr = '';
275	
276	      child.stdout?.on('data', (data) => {
277	        stdout += data.toString();
278	      });
279	
280	      child.stderr?.on('data', (data) => {
281	        const text = data.toString();
282	        stderr += text;
283	        // Show Java logs in real-time
284	        if (text.includes('INFO:') || text.includes('ERROR:') || text.includes('DEBUG:')) {
285	          console.log(`[Java] ${text.trim()}`);
286	        }
287	      });
288	
289	      child.on('close', (code) => {
290	        if (code === 0) {
291	          resolve({ stdout, stderr });
292	        } else {
293	          reject(new Error(`Maven process exited with code ${code}. stderr: ${stderr}`));
294	        }
295	      });
296	
297	      child.on('error', (error) => {
298	        reject(new Error(`Failed to spawn Maven process: ${error.message}`));
299	      });
300	
301	      // Write input data to stdin and close it
302	      child.stdin?.write(inputData);
303	      child.stdin?.end();
304	
305	      // Set timeout
306	      setTimeout(() => {
307	        child.kill();
308	        reject(new Error('Maven process timed out after 2 minutes'));
309	      }, 120000);
310	    });
311	    
312	    if (stderr && stderr.trim()) {
313	      console.warn(`Maven analyzer stderr: ${stderr}`);
314	    }
315	    
316	    // Check if process completed successfully
317	    if (!stdout.includes('SUCCESS:')) {
318	      throw new Error('Maven analyzer did not complete successfully');
319	    }
320	    
321	    // Read JSON output from the file
322	    try {
323	      const jsonContent = readFileSync(outputFile, 'utf8');
324	      
325	      // Clean up the temp file
326	      try {
327	        unlinkSync(outputFile);
328	      } catch (e) {
329	        console.warn(`Could not delete temp file ${outputFile}: ${e.message}`);
330	      }
331	      
332	      return JSON.parse(jsonContent);
333	    } catch (error) {
334	      throw new Error(`Failed to read output file ${outputFile}: ${error.message}`);
335	    }
336	    
337	  } catch (error) {
338	    throw new Error(`Failed to execute Maven analyzer in batch mode: ${error.message}`);
339	  }
340	}
341	
342	/**
343	 * Build classpath including Maven dependencies
344	 */
345	function buildClasspath(javaAnalyzerPath: string): string {
346	  const classpathParts: string[] = [javaAnalyzerPath];
347	
348	  // If it's a JAR file, just return it
349	  if (javaAnalyzerPath.endsWith('.jar')) {
350	    return javaAnalyzerPath;
351	  }
352	
353	  // For compiled classes, we need to include Maven dependencies
354	  const mavenScriptDir = dirname(javaAnalyzerPath); // target/classes -> target
355	  const projectDir = dirname(mavenScriptDir);       // target -> project root
356	
357	  try {
358	    // Use Maven to build dependency classpath
359	    const command = 'mvn dependency:build-classpath -q -Dmdep.outputFile=/dev/stdout';
360	    const dependencyClasspath = execSync(command, {
361	      cwd: projectDir,
362	      encoding: 'utf8',
363	      timeout: 30000,
364	    }).trim();
365	
366	    if (dependencyClasspath) {
367	      classpathParts.push(dependencyClasspath);
368	    }
369	  } catch (error) {
370	    console.warn('Failed to build Maven dependency classpath, using basic classpath');
371	  }
372	
373	  return classpathParts.join(process.platform === 'win32' ? ';' : ':');
374	}
375	
376	/**
377	 * Find the compiled Java Maven analyzer
378	 */
379	function findJavaAnalyzer(): string | null {
380	  // Look for the compiled Java program in common locations
381	  const possiblePaths = [
382	    join(workspaceRoot, 'maven-script/target/classes'),
383	    join(workspaceRoot, 'tools/maven-script/target/classes'),
384	    join(workspaceRoot, 'scripts/maven-script/target/classes'),
385	    join(__dirname, '../maven-script/target/classes'),
386	  ];
387	
388	  for (const path of possiblePaths) {
389	    if (existsSync(join(path, 'MavenModelReader.class'))) {
390	      return path;
391	    }
392	  }
393	
394	  // Also check if there's a JAR file
395	  const jarPaths = [
396	    join(workspaceRoot, 'maven-script/target/maven-script-1.0-SNAPSHOT.jar'),
397	    join(workspaceRoot, 'tools/maven-script/target/maven-script-1.0-SNAPSHOT.jar'),
398	  ];
399	
400	  for (const jarPath of jarPaths) {
401	    if (existsSync(jarPath)) {
402	      return jarPath;
403	    }
404	  }
405	
406	  return null;
407	}
408	
409	// Simplified function for finding Maven projects
410	function findMavenProjects(): string[] {
411	  try {
412	    const command = 'find . -name "pom.xml" -type f';
413	    const output = execSync(command, {
414	      cwd: workspaceRoot,
415	      encoding: 'utf8',
416	    });
417	
418	    const pomFiles = output.trim().split('\n').filter(Boolean);
419	    return pomFiles.map(pomFile => dirname(pomFile.replace('./', '')))
420	                   .filter(path => path !== '.');
421	  } catch {
422	    return [];
423	  }
424	}
425	
426	// Simplified function for getting project name
427	function getProjectName(projectPath: string): string | null {
428	  return projectPath.split('/').pop() || null;
429	}
430	
431	// Find a project by its Maven artifactId
432	function findProjectByName(artifactId: string, mavenProjects: string[]): string | null {
433	  // Try to find by directory name first (most common case)
434	  for (const projectPath of mavenProjects) {
435	    const dirName = projectPath.split('/').pop();
436	    if (dirName === artifactId) {
437	      return dirName;
438	    }
439	  }
440	
441	  // Try to find by parsing pom.xml artifactId for more accurate matching
442	  for (const projectPath of mavenProjects) {
443	    try {
444	      const pomPath = join(workspaceRoot, projectPath, 'pom.xml');
445	      if (existsSync(pomPath)) {
446	        const pomContent = readFileSync(pomPath, 'utf8');
447	        const artifactIdMatch = pomContent.match(/<artifactId>([^<]+)<\/artifactId>/);
448	        if (artifactIdMatch && artifactIdMatch[1] === artifactId) {
449	          return projectPath.split('/').pop() || artifactId;
450	        }
451	      }
452	    } catch {
453	      // Ignore parsing errors
454	    }
455	  }
456	
457	  return null;
458	}
459	
460	/**
461	 * Detect framework based on dependencies and plugins in the Maven config
462	 */
463	function detectFramework(nxConfig: any): string {
464	  const dependencies = nxConfig.implicitDependencies?.external || [];
465	  const targets = nxConfig.targets || {};
466	
467	  // Check for Spring Boot
468	  if (dependencies.some((dep: string) => dep.includes('spring-boot')) ||
469	      Object.values(targets).some((target: any) =>
470	        target.options?.command?.includes('spring-boot'))) {
471	    return 'spring-boot';
472	  }
473	
474	  // Check for Quarkus
475	  if (dependencies.some((dep: string) => dep.includes('quarkus')) ||
476	      Object.values(targets).some((target: any) =>
477	        target.options?.command?.includes('quarkus'))) {
478	    return 'quarkus';
479	  }
480	
481	  // Check for Micronaut
482	  if (dependencies.some((dep: string) => dep.includes('micronaut'))) {
483	    return 'micronaut';
484	  }
485	
486	  // Check for Jakarta EE
487	  if (dependencies.some((dep: string) => dep.includes('jakarta'))) {
488	    return 'jakarta-ee';
489	  }
490	
491	  // Check for Maven specific patterns
492	  if (Object.keys(targets).includes('serve')) {
493	    return 'web-app';
494	  }
495	
496	  return 'maven';
497	}
498	
499	/**
500	 * Normalize target configurations from Maven analysis
501	 */
502	function normalizeTargets(targets: Record<string, any>, options: MavenPluginOptions): Record<string, TargetConfiguration> {
503	  const normalizedTargets: Record<string, TargetConfiguration> = {};
504	
505	  for (const [name, target] of Object.entries(targets)) {
506	    // Map common Maven targets to Nx conventions
507	    let targetName = name;
508	    if (name === 'package' && options.buildTargetName !== 'package') {
509	      targetName = options.buildTargetName!;
510	    } else if (name === 'test' && options.testTargetName !== 'test') {
511	      targetName = options.testTargetName!;
512	    } else if (name === 'serve' && options.serveTargetName !== 'serve') {
513	      targetName = options.serveTargetName!;
514	    }
515	
516	    normalizedTargets[targetName] = {
517	      executor: target.executor || '@nx/run-commands:run-commands',
518	      options: target.options || {},
519	      inputs: target.inputs,
520	      outputs: target.outputs,
521	      dependsOn: target.dependsOn,
522	      configurations: target.configurations,
523	    };
524	  }
525	
526	  return normalizedTargets;
527	}
528	
529	/**
530	 * Plugin configuration for registration
531	 */
532	export default {
533	  name: 'maven-plugin2',
534	  createNodesV2,
535	  createDependencies, // Re-enabled: now uses cached batch results, no additional processes
536	};
