    it('should filter out maven-script and workspace root pom.xml', async () => {
      const configFiles = [
        'pom.xml',
        'maven-script/pom.xml',
        'extensions/arc/pom.xml',
        'target/some-build/pom.xml',
        'node_modules/something/pom.xml',
