import {
  CreateDependencies,
  CreateNodesV2,
  CreateNodesContextV2,
  CreateNodesResult,
  DependencyType,
  detectPackageManager,
  NxJsonConfiguration,
  TargetConfiguration,
  workspaceRoot,
} from '@nx/devkit';
import { dirname, join, relative } from 'path';
import { existsSync, readFileSync, unlinkSync } from 'fs';
import { exec, execSync, spawn } from 'child_process';
import { promisify } from 'util';

const execAsync = promisify(exec);

export interface MavenPluginOptions {
  buildTargetName?: string;
  testTargetName?: string;
  serveTargetName?: string;
  javaExecutable?: string;
  mavenExecutable?: string;
  compilerArgs?: string[];
}

const DEFAULT_OPTIONS: MavenPluginOptions = {
  buildTargetName: 'build',
  testTargetName: 'test',
  serveTargetName: 'serve',
  javaExecutable: 'java',
  mavenExecutable: 'mvn',
  compilerArgs: [],
};

/**
 * Maven plugin that uses a Java program to analyze pom.xml files
 * and generate Nx project configurations with comprehensive dependencies
 */
export const createNodesV2: CreateNodesV2 = [
  '**/pom.xml',
  async (configFiles, options, context) => {
    const opts: MavenPluginOptions = Object.assign({}, DEFAULT_OPTIONS, options || {});

    console.log(`Maven plugin found ${configFiles.length} total pom.xml files`);

    // Filter out the maven-script pom.xml and build artifact directories  
    const filteredConfigFiles = configFiles.filter(file =>
      !file.includes('maven-script/pom.xml') &&
      !file.includes('target/') &&
      !file.includes('node_modules/') &&
      file !== 'pom.xml' // Exclude workspace root
    );

    // Default to reasonable limit for performance, allow override
    const defaultLimit = 50; // Reasonable default for large repos
    const fileLimit = process.env.NX_MAVEN_LIMIT 
      ? parseInt(process.env.NX_MAVEN_LIMIT) 
      : (filteredConfigFiles.length > defaultLimit ? defaultLimit : undefined);

    if (fileLimit && filteredConfigFiles.length > fileLimit) {
      console.log(`Limiting to first ${fileLimit} files (set NX_MAVEN_LIMIT=0 to process all)`);
      filteredConfigFiles.splice(fileLimit);
    }

    console.log(`Filtered config files: ${filteredConfigFiles.length} files`);

    if (filteredConfigFiles.length === 0) {
      return [];
    }

    try {
      // Process in smaller batches to reduce memory and CPU usage
      const batchSize = 25; // Process 25 projects at a time
      const allResults: Record<string, any> = {};

      for (let i = 0; i < filteredConfigFiles.length; i += batchSize) {
        const batch = filteredConfigFiles.slice(i, i + batchSize);
        console.log(`Processing batch ${Math.floor(i/batchSize) + 1}/${Math.ceil(filteredConfigFiles.length/batchSize)} (${batch.length} projects)`);

        const batchResults = await generateBatchNxConfigFromMavenAsync(batch, opts, context);
        Object.assign(allResults, batchResults);
      }

      console.log(`Generated ${Object.keys(allResults).length} Maven project configurations`);

      // Convert batch results to the expected format
      const tupleResults: Array<[string, any]> = [];

      for (const [projectRoot, nxConfig] of Object.entries(allResults)) {
        // Find the corresponding config file
        const configFile = filteredConfigFiles.find(file => dirname(file) === projectRoot);
        if (!configFile) continue;

        // Cache the batch results for createDependencies to use later
        cachedBatchResults.set(projectRoot, nxConfig);

        // Normalize and validate targets
        const normalizedTargets = normalizeTargets(nxConfig.targets || {}, opts);

        const result = {
          projects: {
            [projectRoot]: {
              name: nxConfig.name || projectRoot.split('/').pop() || 'unknown',
              root: projectRoot,
              sourceRoot: nxConfig.sourceRoot || join(projectRoot, 'src/main/java'),
              projectType: nxConfig.projectType || 'library',
              targets: normalizedTargets,
              tags: nxConfig.tags || [],
              implicitDependencies: nxConfig.implicitDependencies,
              namedInputs: nxConfig.namedInputs,
              metadata: {
                technologies: ['maven', 'java'],
                framework: detectFramework(nxConfig),
              },
            },
          },
        };

        tupleResults.push([configFile, result]);
      }

      console.log(`Generated ${tupleResults.length} valid project configurations`);
      return tupleResults;

    } catch (error) {
      console.warn(`Failed to process Maven projects:`, error.message);
      return [];
    }
  },
];

