            }
            json.append("]");
            return json.toString();
        } else if (obj instanceof Map) {
            return mapToJson((Map<String, Object>) obj);
        } else {
            return "\"" + escapeJsonString(obj.toString()) + "\"";
        }
    }

    private static String escapeJsonString(String str) {
        if (str == null) return "";
        return str.replace("\\", "\\\\")
                  .replace("\"", "\\\"")
                  .replace("\b", "\\b")
                  .replace("\f", "\\f")
                  .replace("\n", "\\n")
                  .replace("\r", "\\r")
                  .replace("\t", "\\t");
    }
}

