  1	import {
  2	    createNodesFromFiles,
  3	    readJsonFile,
  4	    writeJsonFile,
  5	    ProjectConfiguration,
  6	    TargetConfiguration,
  7	    CreateNodesFunction,
  8	    CreateNodesContext,
  9	    CreateDependencies,
 10	} from '@nx/devkit';
 11	import { calculateHashForCreateNodes } from '@nx/devkit/src/utils/calculate-hash-for-create-nodes';
 12	import { existsSync } from 'node:fs';
 13	import { dirname, join } from 'node:path';
 14	import { workspaceDataDirectory } from 'nx/src/utils/cache-directory';
 15	import { hashObject } from 'nx/src/devkit-internals';
 16	import { parsePomXml, MavenReport, MavenProjectInfo, MavenPlugin, findProjectByCoordinatesFromCache } from './parse-pom-xml';
 17	
 18	// Performance caches
 19	const dependencyCache = new Map<string, string[]>();
 20	const projectTypeCache = new Map<string, 'application' | 'library'>();
 21	const frameworkCache = new Map<string, { isQuarkus: boolean; isSpringBoot: boolean }>();
 22	
 23	const MAVEN_CONFIG_GLOB = '**/pom.xml';
 24	const MAVEN_CONFIG_AND_TEST_GLOB = '{**/pom.xml,**/src/test/**/*.java}';
 25	
 26	interface MavenPluginOptions {
 27	    includeSubmodules?: boolean;
 28	    verifyTargetName?: string;
 29	    runTargetName?: string;
 30	    testTargetName?: string;
 31	    compileTargetName?: string;
 32	    packageTargetName?: string;
 33	    installTargetName?: string;
 34	}
 35	
 36	function normalizeOptions(options: MavenPluginOptions): MavenPluginOptions {
 37	    options ??= {};
 38	    options.verifyTargetName ??= 'verify';
 39	    options.runTargetName ??= 'run';
 40	    options.testTargetName ??= 'test';
 41	    options.compileTargetName ??= 'compile';
 42	    options.packageTargetName ??= 'package';
 43	    options.installTargetName ??= 'install';
 44	    return options;
 45	}
 46	
 47	type MavenTargets = Record<string, Partial<ProjectConfiguration>>;
 48	
 49	function readTargetsCache(cachePath: string): MavenTargets {
 50	    return existsSync(cachePath) ? readJsonFile(cachePath) : {};
 51	}
 52	
 53	function writeTargetsToCache(cachePath: string, results: MavenTargets) {
 54	    writeJsonFile(cachePath, results);
 55	}
 56	
 57	export const createNodesV2 = [
 58	    MAVEN_CONFIG_AND_TEST_GLOB,
 59	    async (files, options, context) => {
 60	        console.log(`[Maven Plugin] createNodesV2 called with ${files.length} files`);
 61	        
 62	        const { pomFiles, testFiles } = splitConfigFiles(files);
 63	        console.log(`[Maven Plugin] Processing ${pomFiles.length} pom.xml files and ${testFiles.length} test files`);
 64	        
 65	        const optionsHash = hashObject(options);
 66	        const cachePath = join(workspaceDataDirectory, `maven-${optionsHash}.hash`);
 67	        const targetsCache = readTargetsCache(cachePath);
 68	
 69	        console.log(`[Maven Plugin] Parsing POM files for project creation...`);
 70	        const startTime = Date.now();
 71	        const mavenReport = await parsePomXml(context.workspaceRoot, pomFiles);
 72	        const parseTime = Date.now() - startTime;
 73	        console.log(`[Maven Plugin] Parsed ${mavenReport.projects.size} POMs in ${parseTime}ms`);
 74	        
 75	        const mavenProjectRootToTestFilesMap = getMavenProjectRootToTestFilesMap(
 76	            testFiles,
 77	            pomFiles.map((f) => dirname(f))
 78	        );
 79	
 80	        try {
 81	            return createNodesFromFiles(
 82	                makeCreateNodesForMavenConfigFile(
 83	                    mavenReport,
 84	                    targetsCache,
 85	                    mavenProjectRootToTestFilesMap
 86	                ),
 87	                pomFiles,
 88	                options,
 89	                context
 90	            );
 91	        } finally {
 92	            writeTargetsToCache(cachePath, targetsCache);
 93	        }
 94	    },
 95	];
