208	export const makeCreateNodesForMavenConfigFile =
209	    (
210	        mavenReport: MavenReport,
211	        targetsCache: MavenTargets = {},
212	        mavenProjectRootToTestFilesMap: Record<string, string[]> = {}
213	    ): CreateNodesFunction =>
214	        async (
215	            pomFilePath,
216	            options: MavenPluginOptions | undefined,
217	            context: CreateNodesContext
218	        ) => {
219	            const projectRoot = dirname(pomFilePath);
220	            options = normalizeOptions(options);
221	
222	            const hash = await calculateHashForCreateNodes(
223	                projectRoot,
224	                options ?? {},
225	                context
226	            );
227	            targetsCache[hash] ??= await createMavenProject(
228	                mavenReport,
229	                pomFilePath,
230	                options,
231	                context,
232	                mavenProjectRootToTestFilesMap[projectRoot]
233	            );
234	            const project = targetsCache[hash];
235	            if (!project) {
236	                return {};
237	            }
238	            return {
239	                projects: {
240	                    [projectRoot]: project,
241	                },
242	            };
243	        };
244	
245	async function createMavenProject(
246	    mavenReport: MavenReport,
247	    pomFilePath: string,
248	    options: MavenPluginOptions | undefined,
249	    context: CreateNodesContext,
250	    testFiles: string[] = []
251	) {
252	    try {
253	        const projectInfo = mavenReport.projects.get(pomFilePath);
254	        if (!projectInfo) {
255	            return;
256	        }
257	
258	        const { targets, targetGroups } = await createMavenTargets(
259	            projectInfo,
260	            options,
261	            context,
262	            pomFilePath,
263	            testFiles
264	        );
265	
266	        const implicitDependencies = createImplicitDependencies(projectInfo, mavenReport, pomFilePath);
267	
268	        const project: Partial<ProjectConfiguration> = {
269	            name: `${projectInfo.groupId ?? projectInfo.parent?.groupId}.${
270	                projectInfo.artifactId
271	            }`,
272	            projectType: determineProjectType(projectInfo),
273	            targets,
274	            implicitDependencies,
275	            metadata: {
276	                targetGroups,
277	                technologies: ['maven'],
278	                maven: {
279	                    groupId: projectInfo.groupId ?? projectInfo.parent?.groupId,
280	                    artifactId: projectInfo.artifactId,
281	                    version: projectInfo.version ?? projectInfo.parent?.version,
282	                    packaging: projectInfo.packaging,
283	                },
284	            } as any,
285	        };
286	
287	        return project;
288	    } catch (e) {
289	        console.error(e);
290	        return undefined;
291	    }
292	}
