294	async function createMavenTargets(
295	    projectInfo: MavenProjectInfo,
296	    options: MavenPluginOptions | undefined,
297	    context: CreateNodesContext,
298	    pomFilePath: string,
299	    testFiles: string[] = []
300	): Promise<{
301	    targetGroups: Record<string, string[]>;
302	    targets: Record<string, TargetConfiguration>;
303	}> {
304	    const targets: Record<string, TargetConfiguration> = {};
305	    const targetGroups: Record<string, string[]> = {
306	        build: [],
307	        test: [],
308	        dev: [],
309	        quality: [],
310	    };
311	
312	    const projectRoot = dirname(pomFilePath);
313	    const hasTests = testFiles.length > 0;
314	    const isQuarkusProject = hasQuarkusPlugin(projectInfo);
315	    const isSpringBootProject = hasSpringBootPlugin(projectInfo);
316	
317	    // Create enhanced inputs/outputs based on project structure
318	    const commonInputs = getProjectInputs(projectInfo, projectRoot);
319	    const commonOutputs = getProjectOutputs(projectInfo, projectRoot);
320	
321	    // Add standard Maven lifecycle targets with enhanced configuration
322	    const lifecycleTargets = [
323	        {
324	            name: options.compileTargetName,
325	            phase: 'compile',
326	            group: 'build',
327	            outputs: ['{projectRoot}/target/classes'],
328	        },
329	        {
330	            name: options.testTargetName,
331	            phase: 'test',
332	            group: 'test',
333	            outputs: hasTests ? ['{projectRoot}/target/test-results', '{projectRoot}/target/surefire-reports'] : undefined,
334	            condition: hasTests,
335	        },
336	        {
337	            name: options.packageTargetName,
338	            phase: 'package',
339	            group: 'build',
340	            outputs: [`{projectRoot}/target/*.${projectInfo.packaging || 'jar'}`],
341	        },
342	        {
343	            name: options.verifyTargetName,
344	            phase: 'verify',
345	            group: 'quality',
346	            outputs: undefined,
347	        },
348	        {
349	            name: options.installTargetName,
350	            phase: 'install',
351	            group: 'build',
352	            outputs: undefined,
353	        },
354	    ];
355	
356	    // Add lifecycle targets
357	    for (const target of lifecycleTargets) {
358	        if (target.condition === false) continue;
359	
360	        const targetConfig: TargetConfiguration = {
361	            command: `mvn ${target.phase}`,
362	            options: {
363	                cwd: projectRoot,
364	            },
365	            cache: true,
366	            inputs: commonInputs,
367	            outputs: target.outputs,
368	            metadata: {
369	                technologies: ['maven'],
370	                description: `Run Maven ${target.phase} phase`,
371	            },
372	        };
373	
374	        targets[target.name] = targetConfig;
375	        targetGroups[target.group].push(target.name);
376	    }
377	
378	    // Add framework-specific targets
379	    if (isQuarkusProject) {
380	        addQuarkusTargets(targets, targetGroups, projectRoot, options);
381	    }
382	
383	    if (isSpringBootProject) {
384	        addSpringBootTargets(targets, targetGroups, projectRoot, options);
385	    }
386	
387	    // Add plugin-specific targets based on Maven plugins
388	    addPluginSpecificTargets(projectInfo, targets, targetGroups, projectRoot, options);
389	
390	    return { targets, targetGroups };
391	}
392	
393	function hasQuarkusPlugin(projectInfo: MavenProjectInfo): boolean {
394	    return projectInfo.plugins?.some(
395	        plugin => plugin.groupId === 'io.quarkus' && plugin.artifactId === 'quarkus-maven-plugin'
396	    ) || false;
397	}
398	
399	function hasSpringBootPlugin(projectInfo: MavenProjectInfo): boolean {
400	    return projectInfo.plugins?.some(
401	        plugin => plugin.groupId === 'org.springframework.boot' && plugin.artifactId === 'spring-boot-maven-plugin'
402	    ) || false;
403	}
404	
405	function getProjectInputs(projectInfo: MavenProjectInfo, projectRoot: string): string[] {
406	    const inputs = ['default', '^production'];
407	    
408	    // Add source directories
409	    inputs.push('{projectRoot}/src/main/**/*');
410	    
411	    // Add test directories if they exist
412	    inputs.push('{projectRoot}/src/test/**/*');
413	    
414	    // Add resources
415	    inputs.push('{projectRoot}/src/main/resources/**/*');
416	    
417	    return inputs;
418	}
419	
420	function getProjectOutputs(projectInfo: MavenProjectInfo, projectRoot: string): string[] {
421	    return [
422	        '{projectRoot}/target/**/*',
423	    ];
424	}
425	
426	function addQuarkusTargets(
427	    targets: Record<string, TargetConfiguration>,
428	    targetGroups: Record<string, string[]>,
429	    projectRoot: string,
430	    options: MavenPluginOptions | undefined
431	): void {
432	    // Quarkus dev mode
433	    targets['dev'] = {
434	        command: 'mvn quarkus:dev',
435	        options: { cwd: projectRoot },
436	        cache: false,
437	        inputs: ['{projectRoot}/src/**/*', '{projectRoot}/pom.xml'],
438	        metadata: {
439	            technologies: ['maven', 'quarkus'],
440	            description: 'Start Quarkus in development mode',
441	        },
442	    };
443	    targetGroups.dev.push('dev');
444	
445	    // Quarkus native build
446	    targets['build-native'] = {
447	        command: 'mvn package -Dnative',
448	        options: { cwd: projectRoot },
449	        cache: true,
450	        inputs: ['{projectRoot}/src/**/*', '{projectRoot}/pom.xml'],
451	        outputs: ['{projectRoot}/target/*-runner'],
452	        metadata: {
453	            technologies: ['maven', 'quarkus'],
454	            description: 'Build native executable with Quarkus',
455	        },
456	    };
457	    targetGroups.build.push('build-native');
458	}
459	
460	function addSpringBootTargets(
461	    targets: Record<string, TargetConfiguration>,
462	    targetGroups: Record<string, string[]>,
463	    projectRoot: string,
464	    options: MavenPluginOptions | undefined
465	): void {
466	    // Spring Boot run
467	    targets['run'] = {
468	        command: 'mvn spring-boot:run',
469	        options: { cwd: projectRoot },
470	        cache: false,
471	        inputs: ['{projectRoot}/src/**/*', '{projectRoot}/pom.xml'],
472	        metadata: {
473	            technologies: ['maven', 'spring-boot'],
474	            description: 'Run Spring Boot application',
475	        },
476	    };
477	    targetGroups.dev.push('run');
478	}
479	
480	function addPluginSpecificTargets(
481	    projectInfo: MavenProjectInfo,
482	    targets: Record<string, TargetConfiguration>,
483	    targetGroups: Record<string, string[]>,
484	    projectRoot: string,
485	    options: MavenPluginOptions | undefined
486	): void {
487	    if (!projectInfo.plugins) return;
488	
489	    for (const plugin of projectInfo.plugins) {
490	        // Flyway plugin
491	        if (plugin.groupId === 'org.flywaydb' && plugin.artifactId === 'flyway-maven-plugin') {
492	            targets['db-migrate'] = {
493	                command: 'mvn flyway:migrate',
494	                options: { cwd: projectRoot },
495	                cache: false,
496	                inputs: ['{projectRoot}/src/main/resources/db/migration/**/*'],
497	                metadata: {
498	                    technologies: ['maven', 'flyway'],
499	                    description: 'Run database migrations',
500	                },
501	            };
502	            targetGroups.build.push('db-migrate');
503	        }
504	
505	        // Liquibase plugin
506	        if (plugin.groupId === 'org.liquibase' && plugin.artifactId === 'liquibase-maven-plugin') {
507	            targets['db-update'] = {
508	                command: 'mvn liquibase:update',
509	                options: { cwd: projectRoot },
510	                cache: false,
511	                inputs: ['{projectRoot}/src/main/resources/db/changelog/**/*'],
512	                metadata: {
513	                    technologies: ['maven', 'liquibase'],
514	                    description: 'Update database schema',
515	                },
516	            };
517	            targetGroups.build.push('db-update');
518	        }
519	
520	        // Spotless plugin for code formatting
521	        if (plugin.groupId === 'com.diffplug.spotless' && plugin.artifactId === 'spotless-maven-plugin') {
522	            targets['format'] = {
523	                command: 'mvn spotless:apply',
524	                options: { cwd: projectRoot },
525	                cache: false,
526	                inputs: ['{projectRoot}/src/**/*'],
527	                metadata: {
528	                    technologies: ['maven', 'spotless'],
529	                    description: 'Format code with Spotless',
530	                },
531	            };
532	            targetGroups.quality.push('format');
533	        }
534	
535	        // Maven Checkstyle plugin
536	        if (plugin.groupId === 'org.apache.maven.plugins' && plugin.artifactId === 'maven-checkstyle-plugin') {
537	            targets['checkstyle'] = {
538	                command: 'mvn checkstyle:check',
539	                options: { cwd: projectRoot },
540	                cache: true,
541	                inputs: ['{projectRoot}/src/**/*'],
542	                outputs: ['{projectRoot}/target/checkstyle-result.xml'],
543	                metadata: {
544	                    technologies: ['maven', 'checkstyle'],
545	                    description: 'Run Checkstyle code analysis',
546	                },
547	            };
548	            targetGroups.quality.push('checkstyle');
549	        }
550	
551	        // SpotBugs plugin
552	        if (plugin.groupId === 'com.github.spotbugs' && plugin.artifactId === 'spotbugs-maven-plugin') {
553	            targets['spotbugs'] = {
554	                command: 'mvn spotbugs:check',
555	                options: { cwd: projectRoot },
556	                cache: true,
557	                inputs: ['{projectRoot}/src/**/*'],
558	                outputs: ['{projectRoot}/target/spotbugsXml.xml'],
559	                metadata: {
560	                    technologies: ['maven', 'spotbugs'],
561	                    description: 'Run SpotBugs static analysis',
562	                },
563	            };
564	            targetGroups.quality.push('spotbugs');
565	        }
566	    }
567	}
