569	// Optimized version using coordinate map instead of linear search
570	function createProjectDependenciesOptimized(
571	    projectInfo: MavenProjectInfo,
572	    coordinateMap: Map<string, string>,
573	    includeTestDependencies: boolean = false
574	): string[] {
575	    if (!projectInfo.dependencies) return [];
576	
577	    const dependencies: string[] = [];
578	    
579	    for (const dep of projectInfo.dependencies) {
580	        // Include compile, runtime, and optionally test dependencies
581	        const includeThisDep = dep.scope === 'compile' || 
582	                              dep.scope === 'runtime' || 
583	                              !dep.scope || // Default scope is compile
584	                              (includeTestDependencies && dep.scope === 'test');
585	        
586	        if (!includeThisDep) continue;
587	        
588	        // Fast coordinate lookup
589	        const coordinate = `${dep.groupId}.${dep.artifactId}`;
590	        const dependencyProjectName = coordinateMap.get(coordinate);
591	        if (dependencyProjectName) {
592	            dependencies.push(dependencyProjectName);
593	        }
594	    }
595	
596	    return dependencies;
597	}
598	
599	function createProjectDependencies(
600	    projectInfo: MavenProjectInfo,
601	    mavenReport: MavenReport,
602	    includeTestDependencies: boolean = false
603	): string[] {
604	    if (!projectInfo.dependencies) return [];
605	
606	    const dependencies: string[] = [];
607	    
608	    for (const dep of projectInfo.dependencies) {
609	        // Include compile, runtime, and optionally test dependencies
610	        const includeThisDep = dep.scope === 'compile' || 
611	                              dep.scope === 'runtime' || 
612	                              !dep.scope || // Default scope is compile
613	                              (includeTestDependencies && dep.scope === 'test');
614	        
615	        if (!includeThisDep) continue;
616	        
617	        // Try to find a matching project in the workspace
618	        const dependencyProjectName = findProjectByMavenCoordinates(dep.groupId, dep.artifactId, mavenReport);
619	        if (dependencyProjectName) {
620	            dependencies.push(dependencyProjectName);
621	        }
622	    }
623	
624	    return dependencies;
625	}
626	
627	function createImplicitDependencies(
628	    projectInfo: MavenProjectInfo,
629	    mavenReport: MavenReport,
630	    pomFilePath: string
631	): string[] {
632	    const implicitDeps: string[] = [];
633	
634	    // Add parent as implicit dependency
635	    if (projectInfo.parent) {
636	        const parentProjectName = findProjectByMavenCoordinates(
637	            projectInfo.parent.groupId,
638	            '', // Parent might not have artifactId in our data structure
639	            mavenReport
640	        );
641	        if (parentProjectName) {
642	            implicitDeps.push(parentProjectName);
643	        }
644	    }
645	
646	    // Add modules as implicit dependencies (for aggregator projects)
647	    if (projectInfo.modules) {
648	        const projectRoot = dirname(pomFilePath);
649	        for (const module of projectInfo.modules) {
650	            const modulePath = join(projectRoot, module, 'pom.xml');
651	            const moduleProject = mavenReport.projects.get(modulePath);
652	            if (moduleProject) {
653	                const moduleProjectName = `${moduleProject.groupId ?? moduleProject.parent?.groupId}.${moduleProject.artifactId}`;
654	                implicitDeps.push(moduleProjectName);
655	            }
656	        }
657	    }
658	
659	    return implicitDeps;
660	}
661	
662	function findProjectByMavenCoordinates(
663	    groupId: string,
664	    artifactId: string,
665	    mavenReport: MavenReport
666	): string | null {
667	    for (const [pomPath, project] of mavenReport.projects) {
668	        const projectGroupId = project.groupId ?? project.parent?.groupId;
669	        if (projectGroupId === groupId && project.artifactId === artifactId) {
670	            return `${projectGroupId}.${project.artifactId}`;
671	        }
672	    }
673	    return null;
674	}
675	
676	function determineProjectType(projectInfo: MavenProjectInfo): 'application' | 'library' {
677	    // POM packaging usually indicates an aggregator/parent project
678	    if (projectInfo.packaging === 'pom') {
679	        return 'library';
680	    }
681	    
682	    // Common application packaging types
683	    if (['jar', 'war', 'ear'].includes(projectInfo.packaging)) {
684	        // Check if it has a main class or is a web application
685	        if (projectInfo.packaging === 'war' || projectInfo.packaging === 'ear') {
686	            return 'application';
687	        }
688	        
689	        // For JAR, we need to determine if it's a library or application
690	        // Look for common patterns that indicate an application
691	        const isApplication = projectInfo.dependencies?.some(dep => 
692	            // Spring Boot applications
693	            (dep.groupId === 'org.springframework.boot' && dep.artifactId === 'spring-boot-starter') ||
694	            // Quarkus applications  
695	            (dep.groupId === 'io.quarkus' && dep.artifactId.includes('quarkus-')) ||
696	            // Main class indicators
697	            dep.artifactId.includes('main') || dep.artifactId.includes('app')
698	        ) ?? false;
699	        
700	        return isApplication ? 'application' : 'library';
701	    }
702	    
703	    return 'library';
704	}
705	
706	function splitConfigFiles(files: readonly string[]) {
707	    const pomFiles = files
708	        .filter((f) => f.endsWith('pom.xml'))
709	        .filter((f) => !isTestOrTemplateDirectory(f));
710	    const testFiles = files.filter((f) => f.includes('/src/test/'));
711	    return { pomFiles, testFiles };
712	}
713	
714	function isTestOrTemplateDirectory(filePath: string): boolean {
715	    const excludePatterns = [
716	        '/src/test/resources',
717	        '/maven-archetype/',
718	        '/resources-filtered/',
719	        '/test/resources',
720	        'archetype-resources',
721	        '/templates/',
722	    ];
723	    
724	    return excludePatterns.some(pattern => filePath.includes(pattern));
725	}
726	
727	function getMavenProjectRootToTestFilesMap(
728	    testFiles: string[],
729	    projectRoots: string[]
730	): Record<string, string[]> {
731	    const map: Record<string, string[]> = {};
732	    for (const root of projectRoots) {
733	        map[root] = testFiles.filter((f) => f.startsWith(root));
734	    }
735	    return map;
736	}
