 97	export const createDependencies: CreateDependencies = [
 98	    MAVEN_CONFIG_GLOB,
 99	    async (options, context) => {
100	        const files = context.filesToProcess;
101	    console.log(`[Maven Plugin] createDependencies called with ${files?.length || 0} files`);
102	    
103	    if (!files || files.length === 0) {
104	        console.log('[Maven Plugin] No files provided, returning empty dependencies');
105	        return [];
106	    }
107	    
108	    const { pomFiles } = splitConfigFiles(files.filter(f => f.endsWith('pom.xml')));
109	    console.log(`[Maven Plugin] Found ${pomFiles.length} pom.xml files to process`);
110	    
111	    if (pomFiles.length === 0) {
112	        console.log('[Maven Plugin] No pom.xml files found, returning empty dependencies');
113	        return [];
114	    }
115	    
116	    const startTime = Date.now();
117	    console.log(`[Maven Plugin] Parsing ${pomFiles.length} POM files...`);
118	    const mavenReport = await parsePomXml(context.workspaceRoot, pomFiles);
119	    const parseTime = Date.now() - startTime;
120	    console.log(`[Maven Plugin] Parsed ${mavenReport.projects.size} projects in ${parseTime}ms`);
121	    
122	    const dependencies = [];
123	    let processedProjects = 0;
124	    const depStartTime = Date.now();
125	    
126	    // Pre-build coordinate lookup map for faster dependency resolution
127	    const coordinateMap = new Map<string, string>();
128	    for (const [pomFile, projectInfo] of mavenReport.projects) {
129	        const coordinate = `${projectInfo.groupId ?? projectInfo.parent?.groupId}.${projectInfo.artifactId}`;
130	        coordinateMap.set(coordinate, coordinate);
131	    }
132	    
133	    for (const pomFile of pomFiles) {
134	        const projectInfo = mavenReport.projects.get(pomFile);
135	        if (!projectInfo) continue;
136	        
137	        processedProjects++;
138	        if (processedProjects % 200 === 0) {
139	            console.log(`[Maven Plugin] Processed ${processedProjects}/${pomFiles.length} projects...`);
140	        }
141	        
142	        const projectRoot = dirname(pomFile);
143	        const sourceProjectName = `${projectInfo.groupId ?? projectInfo.parent?.groupId}.${projectInfo.artifactId}`;
144	        
145	        // Use cached dependency resolution
146	        const cacheKey = `${sourceProjectName}-deps`;
147	        let allDependencies = dependencyCache.get(cacheKey);
148	        
149	        if (!allDependencies) {
150	            // Create runtime dependencies (compile + runtime scope)
151	            const runtimeDependencies = createProjectDependenciesOptimized(projectInfo, coordinateMap, false);
152	            
153	            // Create test dependencies (test scope) 
154	            const testDependencies = createProjectDependenciesOptimized(projectInfo, coordinateMap, true);
155	            
156	            allDependencies = [...new Set([...runtimeDependencies, ...testDependencies])];
157	            dependencyCache.set(cacheKey, allDependencies);
158	        }
159	        
160	        // Add dependencies
161	        for (const targetProject of allDependencies) {
162	            dependencies.push({
163	                sourceProjectName,
164	                targetProjectName: targetProject,
165	                dependencyType: 'static' as const,
166	                source: pomFile,
167	            });
168	        }
169	        
170	        // Create dependencies from parent relationships
171	        if (projectInfo.parent) {
172	            const parentCoordinate = `${projectInfo.parent.groupId}.`;
173	            const parentProjectName = coordinateMap.get(parentCoordinate);
174	            if (parentProjectName) {
175	                dependencies.push({
176	                    sourceProjectName,
177	                    targetProjectName: parentProjectName,
178	                    dependencyType: 'implicit' as const,
179	                    source: pomFile,
180	                });
181	            }
182	        }
183	        
184	        // Create dependencies from module relationships (parent -> modules)
185	        if (projectInfo.modules) {
186	            for (const module of projectInfo.modules) {
187	                const modulePath = join(projectRoot, module, 'pom.xml');
188	                const moduleProject = mavenReport.projects.get(modulePath);
189	                if (moduleProject) {
190	                    const moduleProjectName = `${moduleProject.groupId ?? moduleProject.parent?.groupId}.${moduleProject.artifactId}`;
191	                    dependencies.push({
192	                        sourceProjectName,
193	                        targetProjectName: moduleProjectName,
194	                        dependencyType: 'implicit' as const,
195	                        source: pomFile,
196	                    });
197	                }
198	            }
199	        }
200	    }
201	    
202	    const depTime = Date.now() - depStartTime;
203	    console.log(`[Maven Plugin] Created ${dependencies.length} dependencies from ${processedProjects} projects in ${depTime}ms`);
204	    return dependencies;
205	    }
206	];
