294	async function createMavenTargets(
295	    projectInfo: MavenProjectInfo,
296	    options: MavenPluginOptions | undefined,
297	    context: CreateNodesContext,
298	    pomFilePath: string,
299	    testFiles: string[] = []
300	): Promise<{
301	    targetGroups: Record<string, string[]>;
302	    targets: Record<string, TargetConfiguration>;
303	}> {
304	    const targets: Record<string, TargetConfiguration> = {};
305	    const targetGroups: Record<string, string[]> = {
306	        build: [],
307	        test: [],
308	        dev: [],
309	        quality: [],
310	    };
311	
312	    const projectRoot = dirname(pomFilePath);
313	    const hasTests = testFiles.length > 0;
314	    const isQuarkusProject = hasQuarkusPlugin(projectInfo);
315	    const isSpringBootProject = hasSpringBootPlugin(projectInfo);
316	
317	    // Create enhanced inputs/outputs based on project structure
318	    const commonInputs = getProjectInputs(projectInfo, projectRoot);
319	    const commonOutputs = getProjectOutputs(projectInfo, projectRoot);
320	
321	    // Add standard Maven lifecycle targets with enhanced configuration
322	    const lifecycleTargets = [
323	        {
324	            name: options.compileTargetName,
325	            phase: 'compile',
326	            group: 'build',
327	            outputs: ['{projectRoot}/target/classes'],
328	        },
329	        {
330	            name: options.testTargetName,
331	            phase: 'test',
332	            group: 'test',
333	            outputs: hasTests ? ['{projectRoot}/target/test-results', '{projectRoot}/target/surefire-reports'] : undefined,
334	            condition: hasTests,
335	        },
336	        {
337	            name: options.packageTargetName,
338	            phase: 'package',
339	            group: 'build',
340	            outputs: [`{projectRoot}/target/*.${projectInfo.packaging || 'jar'}`],
341	        },
342	        {
343	            name: options.verifyTargetName,
344	            phase: 'verify',
345	            group: 'quality',
346	            outputs: undefined,
347	        },
348	        {
349	            name: options.installTargetName,
350	            phase: 'install',
351	            group: 'build',
352	            outputs: undefined,
353	        },
354	    ];
355	
356	    // Add lifecycle targets
357	    for (const target of lifecycleTargets) {
358	        if (target.condition === false) continue;
359	
360	        const targetConfig: TargetConfiguration = {
361	            command: `mvn ${target.phase}`,
362	            options: {
363	                cwd: projectRoot,
364	            },
365	            cache: true,
366	            inputs: commonInputs,
367	            outputs: target.outputs,
368	            metadata: {
369	                technologies: ['maven'],
370	                description: `Run Maven ${target.phase} phase`,
371	            },
372	        };
373	
374	        targets[target.name] = targetConfig;
375	        targetGroups[target.group].push(target.name);
376	    }
377	
378	    // Add framework-specific targets
379	    if (isQuarkusProject) {
380	        addQuarkusTargets(targets, targetGroups, projectRoot, options);
381	    }
382	
383	    if (isSpringBootProject) {
384	        addSpringBootTargets(targets, targetGroups, projectRoot, options);
385	    }
386	
387	    // Add plugin-specific targets based on Maven plugins
388	    addPluginSpecificTargets(projectInfo, targets, targetGroups, projectRoot, options);
389	
390	    return { targets, targetGroups };
391	}
