599	function createProjectDependencies(
600	    projectInfo: MavenProjectInfo,
601	    mavenReport: MavenReport,
602	    includeTestDependencies: boolean = false
603	): string[] {
604	    if (!projectInfo.dependencies) return [];
605	
606	    const dependencies: string[] = [];
607	    
608	    for (const dep of projectInfo.dependencies) {
609	        // Include compile, runtime, and optionally test dependencies
610	        const includeThisDep = dep.scope === 'compile' || 
611	                              dep.scope === 'runtime' || 
612	                              !dep.scope || // Default scope is compile
613	                              (includeTestDependencies && dep.scope === 'test');
614	        
615	        if (!includeThisDep) continue;
616	        
617	        // Try to find a matching project in the workspace
618	        const dependencyProjectName = findProjectByMavenCoordinates(dep.groupId, dep.artifactId, mavenReport);
619	        if (dependencyProjectName) {
620	            dependencies.push(dependencyProjectName);
621	        }
622	    }
623	
624	    return dependencies;
625	}
