627	function createImplicitDependencies(
628	    projectInfo: MavenProjectInfo,
629	    mavenReport: MavenReport,
630	    pomFilePath: string
631	): string[] {
632	    const implicitDeps: string[] = [];
633	
634	    // Add parent as implicit dependency
635	    if (projectInfo.parent) {
636	        const parentProjectName = findProjectByMavenCoordinates(
637	            projectInfo.parent.groupId,
638	            '', // Parent might not have artifactId in our data structure
639	            mavenReport
640	        );
641	        if (parentProjectName) {
642	            implicitDeps.push(parentProjectName);
643	        }
644	    }
645	
646	    // Add modules as implicit dependencies (for aggregator projects)
647	    if (projectInfo.modules) {
648	        const projectRoot = dirname(pomFilePath);
649	        for (const module of projectInfo.modules) {
650	            const modulePath = join(projectRoot, module, 'pom.xml');
651	            const moduleProject = mavenReport.projects.get(modulePath);
652	            if (moduleProject) {
653	                const moduleProjectName = `${moduleProject.groupId ?? moduleProject.parent?.groupId}.${moduleProject.artifactId}`;
654	                implicitDeps.push(moduleProjectName);
655	            }
656	        }
657	    }
658	
659	    return implicitDeps;
660	}
