662	function findProjectByMavenCoordinates(
663	    groupId: string,
664	    artifactId: string,
665	    mavenReport: MavenReport
666	): string | null {
667	    for (const [pomPath, project] of mavenReport.projects) {
668	        const projectGroupId = project.groupId ?? project.parent?.groupId;
669	        if (projectGroupId === groupId && project.artifactId === artifactId) {
670	            return `${projectGroupId}.${project.artifactId}`;
671	        }
672	    }
673	    return null;
674	}
