676	function determineProjectType(projectInfo: MavenProjectInfo): 'application' | 'library' {
677	    // POM packaging usually indicates an aggregator/parent project
678	    if (projectInfo.packaging === 'pom') {
679	        return 'library';
680	    }
681	    
682	    // Common application packaging types
683	    if (['jar', 'war', 'ear'].includes(projectInfo.packaging)) {
684	        // Check if it has a main class or is a web application
685	        if (projectInfo.packaging === 'war' || projectInfo.packaging === 'ear') {
686	            return 'application';
687	        }
688	        
689	        // For JAR, we need to determine if it's a library or application
690	        // Look for common patterns that indicate an application
691	        const isApplication = projectInfo.dependencies?.some(dep => 
692	            // Spring Boot applications
693	            (dep.groupId === 'org.springframework.boot' && dep.artifactId === 'spring-boot-starter') ||
694	            // Quarkus applications  
695	            (dep.groupId === 'io.quarkus' && dep.artifactId.includes('quarkus-')) ||
696	            // Main class indicators
697	            dep.artifactId.includes('main') || dep.artifactId.includes('app')
698	        ) ?? false;
699	        
700	        return isApplication ? 'application' : 'library';
701	    }
702	    
703	    return 'library';
704	}
