707	    const pomFiles = files
708	        .filter((f) => f.endsWith('pom.xml'))
709	        .filter((f) => !isTestOrTemplateDirectory(f));
710	    const testFiles = files.filter((f) => f.includes('/src/test/'));
711	    return { pomFiles, testFiles };
