   1import {
   2    CreateDependencies,
   3    CreateNodesContext,
   4    createNodesFromFiles,
   5    CreateNodesFunction,
   6    ProjectConfiguration,
   7    readJsonFile,
   8    TargetConfiguration,
   9    writeJsonFile,
  10    DependencyType,
  11} from '@nx/devkit';
  12import {calculateHashForCreateNodes} from '@nx/devkit/src/utils/calculate-hash-for-create-nodes';
  13import {existsSync} from 'node:fs';
  14import {dirname, join} from 'node:path';
  15import {workspaceDataDirectory} from 'nx/src/utils/cache-directory';
  16import {hashObject} from 'nx/src/devkit-internals';
  17import {MavenProjectInfo, MavenReport, parsePomXml} from './parse-pom-xml';
  18import {ChildProcess} from 'node:child_process';
  19import type {RawProjectGraphDependency} from "nx/src/project-graph/project-graph-builder";
  20
  21// Performance caches
  22const dependencyCache = new Map<string, string[]>();
  23const projectTypeCache = new Map<string, 'application' | 'library'>();
  24const frameworkCache = new Map<string, { isQuarkus: boolean; isSpringBoot: boolean }>();
  25
  26const MAVEN_CONFIG_GLOB = '**/pom.xml';
  27const MAVEN_CONFIG_AND_TEST_GLOB = '{**/pom.xml,**/src/test/**/*.java}';
  28
  29interface MavenPluginOptions {
  30    includeSubmodules?: boolean;
  31    verifyTargetName?: string;
  32    runTargetName?: string;
  33    testTargetName?: string;
  34    compileTargetName?: string;
  35    packageTargetName?: string;
  36    installTargetName?: string;
  37}
  38
  39function normalizeOptions(options: MavenPluginOptions): MavenPluginOptions {
  40    options ??= {};
  41    options.verifyTargetName ??= 'verify';
  42    options.runTargetName ??= 'run';
  43    options.testTargetName ??= 'test';
  44    options.compileTargetName ??= 'compile';
  45    options.packageTargetName ??= 'package';
  46    options.installTargetName ??= 'install';
  47    return options;
  48}
  49
  50type MavenTargets = Record<string, Partial<ProjectConfiguration>>;
  51
  52function readTargetsCache(cachePath: string): MavenTargets {
  53    return existsSync(cachePath) ? readJsonFile(cachePath) : {};
  54}
  55
  56function writeTargetsToCache(cachePath: string, results: MavenTargets) {
  57    writeJsonFile(cachePath, results);
  58}
  59
  60export const createNodesV2 = [
  61    MAVEN_CONFIG_AND_TEST_GLOB,
  62    async (files, options, context) => {
  63        console.log(`[Maven Plugin] createNodesV2 called with ${files.length} files`);
  64
  65        const {pomFiles, testFiles} = splitConfigFiles(files);
  66        console.log(`[Maven Plugin] Processing ${pomFiles.length} pom.xml files and ${testFiles.length} test files`);
  67
  68        const optionsHash = hashObject(options);
  69        const cachePath = join(workspaceDataDirectory, `maven-${optionsHash}.hash`);
  70        const targetsCache = readTargetsCache(cachePath);
  71
  72        console.log(`[Maven Plugin] Parsing POM files for project creation...`);
  73        const startTime = Date.now();
  74        const mavenReport = await parsePomXml(context.workspaceRoot, pomFiles);
  75        const parseTime = Date.now() - startTime;
  76        console.log(`[Maven Plugin] Parsed ${mavenReport.projects.size} POMs in ${parseTime}ms`);
  77
  78        // Instead of using all globbed files, discover modules recursively from root
  79        console.log(`[Maven Plugin] Discovering Maven modules recursively from root pom.xml...`);
  80        const actualPomFiles = await discoverMavenModules(context.workspaceRoot);
  81        console.log(`[Maven Plugin] Found ${actualPomFiles.length} actual Maven projects (vs ${pomFiles.length} globbed)`);
  82
  83        // Parse only the actual projects
  84        const actualMavenReport = await parsePomXml(context.workspaceRoot, actualPomFiles);
  85        console.log(`[Maven Plugin] Parsed ${actualMavenReport.projects.size} actual Maven projects`);
  86
  87        const mavenProjectRootToTestFilesMap = getMavenProjectRootToTestFilesMap(
  88            testFiles,
  89            actualPomFiles.map((f) => dirname(f))
  90        );
  91
  92        // Collect and dedupe all unknown plugins before discovery
  93        console.log(`[Maven Plugin] Collecting unique plugins for batch discovery...`);
  94        const uniquePlugins = collectUniquePlugins(actualMavenReport, actualPomFiles);
  95        console.log(`[Maven Plugin] Found ${uniquePlugins.size} unique unknown plugins to discover`);
  96
  97        // Skip plugin discovery for now to improve performance
  98        console.log(`[Maven Plugin] Skipping plugin discovery for performance - using known plugins only`);
  99        const discoveredPlugins = new Map<string, DiscoveredPlugin>();
 100
 101        try {
 102            return createNodesFromFiles(
 103                makeCreateNodesForMavenConfigFile(
 104                    actualMavenReport,
 105                    targetsCache,
 106                    mavenProjectRootToTestFilesMap,
 107                    discoveredPlugins
 108                ),
 109                actualPomFiles,
 110                options,
 111                context
 112            );
 113        } finally {
 114            writeTargetsToCache(cachePath, targetsCache);
 115        }
 116    },
 117];
