 119export const createDependencies: CreateDependencies =
 120    async (options, context) => {
 121        // Get all pom.xml files from the workspace - for full dependency analysis
 122        const allProjects = Object.values(context.projects);
 123        const allPomFiles: string[] = [];
 124
 125        for (const project of allProjects) {
 126            const pomPath = `${project.root}/pom.xml`;
 127            // Only include if the pom.xml file actually exists
 128            if (existsSync(pomPath)) {
 129                allPomFiles.push(pomPath);
 130            }
 131        }
 132
 133        console.log(`[Maven Plugin] createDependencies called, processing ${allPomFiles.length} Maven projects`);
 134        console.log(`[Maven Plugin] Available projects in context: ${Object.keys(context.projects).length}`);
 135        const projectNames = Object.values(context.projects).map(p => p.name).slice(0, 5);
 136        console.log(`[Maven Plugin] Sample project names: ${projectNames.join(', ')}`);
 137
 138        if (allPomFiles.length === 0) {
 139            console.log('[Maven Plugin] No Maven projects found, returning empty dependencies');
 140            return [];
 141        }
 142
 143        const {pomFiles} = splitConfigFiles(allPomFiles);
 144        console.log(`[Maven Plugin] Found ${pomFiles.length} pom.xml files to process`);
 145
 146        if (pomFiles.length === 0) {
 147            console.log('[Maven Plugin] No pom.xml files found, returning empty dependencies');
 148            return [];
 149        }
 150
 151        const startTime = Date.now();
 152        console.log(`[Maven Plugin] Parsing ${pomFiles.length} POM files with selective effective POM usage...`);
 153
 154        // Use effective POM only for core projects that likely have complex inheritance
 155        const useEffectiveForProject = (pomFile: string): boolean => {
 156          return pomFile.includes('/core/') ||
 157                 pomFile.includes('/extensions/') ||
 158                 pomFile.includes('/independent-projects/arc/');
 159        };
 160
 161        // Split POMs into those needing effective parsing vs raw parsing
 162        const effectivePomFiles = pomFiles.filter(useEffectiveForProject);
 163        const rawPomFiles = pomFiles.filter(f => !useEffectiveForProject(f));
 164
 165        console.log(`[Maven Plugin] Using effective POM for ${effectivePomFiles.length} core projects, raw POM for ${rawPomFiles.length} others`);
 166
 167        // Parse both sets and merge
 168        const [effectiveReport, rawReport] = await Promise.all([
 169          effectivePomFiles.length > 0 ? parsePomXml(context.workspaceRoot, effectivePomFiles, true) : { projects: new Map(), projectToModules: new Map() },
 170          rawPomFiles.length > 0 ? parsePomXml(context.workspaceRoot, rawPomFiles, false) : { projects: new Map(), projectToModules: new Map() }
 171        ]);
 172
 173        // Merge the reports
 174        const mavenReport = {
 175          projects: new Map([...effectiveReport.projects, ...rawReport.projects]),
 176          projectToModules: new Map([...effectiveReport.projectToModules, ...rawReport.projectToModules])
 177        };
 178
 179        const parseTime = Date.now() - startTime;
 180        console.log(`[Maven Plugin] Parsed ${mavenReport.projects.size} projects in ${parseTime}ms`);
 181
 182        const dependencies: RawProjectGraphDependency[] = [];
 183        let processedProjects = 0;
 184        const depStartTime = Date.now();
 185
 186        // Pre-build coordinate lookup map for faster dependency resolution
 187        const coordinateMap = new Map<string, string>();
 188        for (const [pomFile, projectInfo] of mavenReport.projects) {
 189            const coordinate = `${projectInfo.groupId ?? projectInfo.parent?.groupId}.${projectInfo.artifactId}`;
 190            coordinateMap.set(coordinate, coordinate);
 191        }
 192
 193        for (const pomFile of pomFiles) {
 194            const projectInfo = mavenReport.projects.get(pomFile);
 195            if (!projectInfo) continue;
 196
 197            processedProjects++;
 198            if (processedProjects % 200 === 0) {
 199                console.log(`[Maven Plugin] Processed ${processedProjects}/${pomFiles.length} projects...`);
 200            }
 201
 202            const projectRoot = dirname(pomFile);
 203            const sourceProjectName = `${projectInfo.groupId ?? projectInfo.parent?.groupId}.${projectInfo.artifactId}`;
 204
 205            // Use cached dependency resolution
 206            const cacheKey = `${sourceProjectName}-deps`;
 207            let allDependencies = dependencyCache.get(cacheKey);
 208
 209            if (!allDependencies) {
 210                // Create runtime dependencies (compile + runtime scope)
 211                const runtimeDependencies = createProjectDependenciesOptimized(projectInfo, coordinateMap, false);
 212
 213                // Create test dependencies (test scope)
 214                const testDependencies = createProjectDependenciesOptimized(projectInfo, coordinateMap, true);
 215
 216                allDependencies = [...new Set([...runtimeDependencies, ...testDependencies])];
 217                dependencyCache.set(cacheKey, allDependencies);
 218            }
 219
 220            // Add dependencies
 221            for (const targetProject of allDependencies) {
 222                // Verify target project exists in context
 223                const targetExists = Object.values(context.projects).some(p =>
 224                    p.name === targetProject ||
 225                    ((p.metadata as any)?.maven && `${(p.metadata as any).maven.groupId}.${(p.metadata as any).maven.artifactId}` === targetProject)
 226                );
 227
 228                if (targetExists) {
 229                    // Make sourceFile relative to workspace root
 230                    const relativeSourceFile = pomFile.startsWith(context.workspaceRoot) 
 231                        ? pomFile.substring(context.workspaceRoot.length + 1)
 232                        : pomFile;
 233                    dependencies.push({
 234                        source: sourceProjectName,
 235                        target: targetProject,
 236                        type: DependencyType.static,
 237                        sourceFile: relativeSourceFile,
 238                    });
 239                    console.log(`[Maven Plugin] Added dependency: ${sourceProjectName} -> ${targetProject}`);
 240                } else {
 241                    console.log(`[Maven Plugin] Skipping dependency to non-existent project: ${targetProject} (source: ${sourceProjectName})`);
 242                }
 243            }
 244
 245            // Create dependencies from parent relationships
 246            if (projectInfo.parent) {
 247                const parentCoordinate = `${projectInfo.parent.groupId}.`;
 248                const parentProjectName = coordinateMap.get(parentCoordinate);
 249                if (parentProjectName && Object.values(context.projects).some(p => p.name === parentProjectName)) {
 250                    // Make sourceFile relative to workspace root
 251                    const relativeSourceFile = pomFile.startsWith(context.workspaceRoot) 
 252                        ? pomFile.substring(context.workspaceRoot.length + 1)
 253                        : pomFile;
 254                    dependencies.push({
 255                        source: sourceProjectName,
 256                        target: parentProjectName,
 257                        type: DependencyType.implicit,
 258                        sourceFile: relativeSourceFile,
 259                    });
 260                }
 261            }
 262
 263            // Create dependencies from module relationships (parent -> modules)
 264            if (projectInfo.modules) {
 265                for (const module of projectInfo.modules) {
 266                    const modulePath = join(projectRoot, module, 'pom.xml');
 267                    const moduleProject = mavenReport.projects.get(modulePath);
 268                    if (moduleProject) {
 269                        const moduleProjectName = `${moduleProject.groupId ?? moduleProject.parent?.groupId}.${moduleProject.artifactId}`;
 270                        if (Object.values(context.projects).some(p => p.name === moduleProjectName)) {
 271                            // Make sourceFile relative to workspace root
 272                            const relativeSourceFile = pomFile.startsWith(context.workspaceRoot) 
 273                                ? pomFile.substring(context.workspaceRoot.length + 1)
 274                                : pomFile;
 275                            dependencies.push({
 276                                source: sourceProjectName,
 277                                target: moduleProjectName,
 278                                type: DependencyType.implicit,
 279                                sourceFile: relativeSourceFile,
 280                            });
 281                        }
 282                    }
 283                }
 284            }
 285        }
 286
 287        const depTime = Date.now() - depStartTime;
 288        console.log(`[Maven Plugin] Created ${dependencies.length} dependencies from ${processedProjects} projects in ${depTime}ms`);
 289        console.log(`[Maven Plugin] Sample dependencies: ${dependencies.slice(0, 3).map(d => `${d.source} -> ${d.target}`).join(', ')}`);
 290        console.log(`[Maven Plugin] RETURNING DEPENDENCIES ARRAY TO NX, length: ${dependencies.length}`);
 291
 292        // Validate that all source and target projects exist
 293        const missingSourceProjects = new Set<string>();
 294        const missingTargetProjects = new Set<string>();
 295
 296        for (const dep of dependencies.slice(0, 10)) { // Check first 10 for sample
 297            const sourceExists = Object.values(context.projects).some(p => p.name === dep.source);
 298            const targetExists = Object.values(context.projects).some(p => p.name === dep.target);
 299
 300            if (!sourceExists) {
 301                missingSourceProjects.add(dep.source);
 302            }
 303            if (!targetExists) {
 304                missingTargetProjects.add(dep.target);
 305            }
 306        }
 307
 308        if (missingSourceProjects.size > 0) {
 309            console.log(`[Maven Plugin] WARNING: Missing source projects: ${Array.from(missingSourceProjects).join(', ')}`);
 310        }
 311        if (missingTargetProjects.size > 0) {
 312            console.log(`[Maven Plugin] WARNING: Missing target projects: ${Array.from(missingTargetProjects).join(', ')}`);
 313        }
 314
 315        return dependencies;
 316    };
