 318export const makeCreateNodesForMavenConfigFile =
 319    (
 320        mavenReport: MavenReport,
 321        targetsCache: MavenTargets = {},
 322        mavenProjectRootToTestFilesMap: Record<string, string[]> = {},
 323        discoveredPlugins: Map<string, DiscoveredPlugin> = new Map()
 324    ): CreateNodesFunction =>
 325        async (
 326            pomFilePath,
 327            options: MavenPluginOptions | undefined,
 328            context: CreateNodesContext
 329        ) => {
 330            const projectRoot = dirname(pomFilePath);
 331            options = normalizeOptions(options);
 332
 333            const hash = await calculateHashForCreateNodes(
 334                projectRoot,
 335                options ?? {},
 336                context
 337            );
 338            targetsCache[hash] ??= await createMavenProject(
 339                mavenReport,
 340                pomFilePath,
 341                options,
 342                context,
 343                mavenProjectRootToTestFilesMap[projectRoot],
 344                discoveredPlugins
 345            );
 346            const project = targetsCache[hash];
 347            if (!project) {
 348                return {};
 349            }
 350            return {
 351                projects: {
 352                    [projectRoot]: project,
 353                },
 354            };
 355        };
