 357async function createMavenProject(
 358    mavenReport: MavenReport,
 359    pomFilePath: string,
 360    options: MavenPluginOptions | undefined,
 361    context: CreateNodesContext,
 362    testFiles: string[] = [],
 363    discoveredPlugins: Map<string, DiscoveredPlugin> = new Map()
 364) {
 365    try {
 366        const projectInfo = mavenReport.projects.get(pomFilePath);
 367        if (!projectInfo) {
 368            return;
 369        }
 370
 371        const {targets, targetGroups} = await createMavenTargets(
 372            projectInfo,
 373            options,
 374            context,
 375            pomFilePath,
 376            testFiles,
 377            discoveredPlugins
 378        );
 379
 380        const implicitDependencies = createImplicitDependencies(projectInfo, mavenReport, pomFilePath);
 381
 382        const projectName = `${projectInfo.groupId ?? projectInfo.parent?.groupId}.${
 383            projectInfo.artifactId
 384        }`;
 385        console.log(`[Maven Plugin] Creating project: ${projectName} at ${dirname(pomFilePath)}`);
 386
 387        const project: Partial<ProjectConfiguration> = {
 388            name: projectName,
 389            projectType: determineProjectType(projectInfo),
 390            targets,
 391            implicitDependencies,
 392            metadata: {
 393                targetGroups,
 394                technologies: ['maven'],
 395                maven: {
 396                    groupId: projectInfo.groupId ?? projectInfo.parent?.groupId,
 397                    artifactId: projectInfo.artifactId,
 398                    version: projectInfo.version ?? projectInfo.parent?.version,
 399                    packaging: projectInfo.packaging,
 400                },
 401            } as any,
 402        };
 403
 404        return project;
 405    } catch (e) {
 406        console.error(e);
 407        return undefined;
 408    }
 409}
