 411async function createMavenTargets(
 412    projectInfo: MavenProjectInfo,
 413    options: MavenPluginOptions | undefined,
 414    context: CreateNodesContext,
 415    pomFilePath: string,
 416    testFiles: string[] = [],
 417    discoveredPlugins: Map<string, DiscoveredPlugin> = new Map()
 418): Promise<{
 419    targetGroups: Record<string, string[]>;
 420    targets: Record<string, TargetConfiguration>;
 421}> {
 422    const targets: Record<string, TargetConfiguration> = {};
 423    const targetGroups: Record<string, string[]> = {
 424        build: [],
 425        test: [],
 426        dev: [],
 427        quality: [],
 428    };
 429
 430    const projectRoot = dirname(pomFilePath);
 431    const hasTests = testFiles.length > 0;
 432
 433    // Create enhanced inputs/outputs based on project structure
 434    const commonInputs = getProjectInputs(projectInfo, projectRoot);
 435    const commonOutputs = getProjectOutputs(projectInfo, projectRoot);
 436
 437    // Add standard Maven lifecycle targets with enhanced configuration
 438    const lifecycleTargets = [
 439        {
 440            name: options.compileTargetName,
 441            phase: 'compile',
 442            group: 'build',
 443            outputs: ['{projectRoot}/target/classes'],
 444        },
 445        {
 446            name: options.testTargetName,
 447            phase: 'test',
 448            group: 'test',
 449            outputs: hasTests ? ['{projectRoot}/target/test-results', '{projectRoot}/target/surefire-reports'] : undefined,
 450            condition: hasTests,
 451        },
 452        {
 453            name: options.packageTargetName,
 454            phase: 'package',
 455            group: 'build',
 456            outputs: [`{projectRoot}/target/*.${projectInfo.packaging || 'jar'}`],
 457        },
 458        {
 459            name: options.verifyTargetName,
 460            phase: 'verify',
 461            group: 'quality',
 462            outputs: undefined,
 463        },
 464        {
 465            name: options.installTargetName,
 466            phase: 'install',
 467            group: 'build',
 468            outputs: undefined,
 469        },
 470    ];
 471
 472    // Add lifecycle targets
 473    for (const target of lifecycleTargets) {
 474        if (target.condition === false) continue;
 475
 476        const targetConfig: TargetConfiguration = {
 477            command: `mvn ${target.phase}`,
 478            options: {
 479                cwd: projectRoot,
 480            },
 481            cache: true,
 482            inputs: commonInputs,
 483            outputs: target.outputs,
 484            metadata: {
 485                technologies: ['maven'],
 486                description: `Run Maven ${target.phase} phase`,
 487            },
 488        };
 489
 490        targets[target.name] = targetConfig;
 491        targetGroups[target.group].push(target.name);
 492    }
 493
 494    // Dynamically discover and add plugin-specific targets
 495    await addDiscoveredPluginTargets(projectInfo, targets, targetGroups, projectRoot, options, discoveredPlugins);
 496
 497    return {targets, targetGroups};
 498}
