 830async function addDiscoveredPluginTargets(
 831    projectInfo: MavenProjectInfo,
 832    targets: Record<string, TargetConfiguration>,
 833    targetGroups: Record<string, string[]>,
 834    projectRoot: string,
 835    options: MavenPluginOptions | undefined,
 836    discoveredPlugins: Map<string, DiscoveredPlugin>
 837): Promise<void> {
 838    if (!projectInfo.plugins) return;
 839
 840    for (const plugin of projectInfo.plugins) {
 841        const pluginKey = `${plugin.groupId}:${plugin.artifactId}`;
 842        const pluginConfig = PLUGIN_CONFIGS.get(pluginKey);
 843
 844        if (pluginConfig) {
 845            // Use known configuration for common plugins
 846            for (const goalConfig of pluginConfig.goals) {
 847                const targetName = getTargetName(plugin.artifactId, goalConfig.goal);
 848                const category = goalConfig.category || 'build';
 849
 850                targets[targetName] = {
 851                    command: `mvn ${pluginKey}:${goalConfig.goal}`,
 852                    options: { cwd: projectRoot },
 853                    cache: shouldCacheTarget(goalConfig.goal),
 854                    inputs: getInputsForPlugin(pluginKey, goalConfig.goal, projectRoot),
 855                    outputs: getOutputsForPlugin(pluginKey, goalConfig.goal, projectRoot),
 856                    metadata: {
 857                        technologies: ['maven', getPluginTechnology(plugin.artifactId)],
 858                        description: goalConfig.description,
 859                    },
 860                };
 861
 862                targetGroups[category] = targetGroups[category] || [];
 863                targetGroups[category].push(targetName);
 864            }
 865        } else {
 866            // Use pre-discovered plugin data
 867            const discoveredPlugin = discoveredPlugins.get(pluginKey);
 868            if (discoveredPlugin) {
 869                for (const goal of discoveredPlugin.goals) {
 870                    const targetName = getTargetName(plugin.artifactId, goal.goal);
 871
 872                    targets[targetName] = {
 873                        command: `mvn ${pluginKey}:${goal.goal}`,
 874                        options: { cwd: projectRoot },
 875                        cache: true, // Default to cached for unknown plugins
 876                        inputs: ['{projectRoot}/src/**/*', '{projectRoot}/pom.xml'],
 877                        metadata: {
 878                            technologies: ['maven'],
 879                            description: goal.description || `Run ${goal.goal} goal`,
 880                        },
 881                    };
 882
 883                    targetGroups.build = targetGroups.build || [];
 884                    targetGroups.build.push(targetName);
 885                }
 886            }
 887        }
 888    }
 889}
