 892function parsePluginDescribeOutput(output: string): PluginGoal[] {
 893    const goals: PluginGoal[] = [];
 894    const lines = output.split('\n');
 895
 896    let inGoalsSection = false;
 897    let foundGoalsHeader = false;
 898
 899    console.log(`[Maven Plugin Discovery] Parsing ${lines.length} lines of output`);
 900
 901    for (let i = 0; i < lines.length; i++) {
 902        const line = lines[i];
 903        const trimmedLine = line.trim();
 904
 905        // Skip empty lines
 906        if (!trimmedLine) continue;
 907
 908        // Look for various goals section indicators
 909        if (!foundGoalsHeader && (
 910            trimmedLine.toLowerCase().includes('goals are available') ||
 911            trimmedLine.toLowerCase().includes('this plugin has') ||
 912            trimmedLine.toLowerCase().includes('goals:') ||
 913            trimmedLine.includes('Mojo:') ||
 914            (trimmedLine.includes(':') && (trimmedLine.includes('goal') || trimmedLine.includes('mojo')))
 915        )) {
 916            foundGoalsHeader = true;
 917            inGoalsSection = true;
 918            console.log(`[Maven Plugin Discovery] Found goals section header at line ${i}: "${trimmedLine}"`);
 919            continue;
 920        }
 921
 922        if (foundGoalsHeader) {
 923            // Try different goal parsing patterns
 924
 925            // Pattern 1: "goalname - description"
 926            let goalMatch = trimmedLine.match(/^([a-zA-Z0-9\-_.]+)\s*-\s*(.+)$/);
 927            if (goalMatch) {
 928                const goal = goalMatch[1].trim();
 929                const description = goalMatch[2].trim();
 930                goals.push({ goal, description });
 931                console.log(`[Maven Plugin Discovery] Found goal (pattern 1): ${goal} - ${description}`);
 932                continue;
 933            }
 934
 935            // Pattern 2: "groupId:artifactId:version:goalname description"
 936            goalMatch = trimmedLine.match(/^[^:]+:[^:]+:[^:]+:([a-zA-Z0-9\-_.]+)(?:\s+(.*))?$/);
 937            if (goalMatch) {
 938                const goal = goalMatch[1].trim();
 939                const description = goalMatch[2]?.trim() || `Run ${goal} goal`;
 940                goals.push({ goal, description });
 941                console.log(`[Maven Plugin Discovery] Found goal (pattern 2): ${goal} - ${description}`);
 942                continue;
 943            }
 944
 945            // Pattern 3: "goalname" (standalone)
 946            goalMatch = trimmedLine.match(/^([a-zA-Z0-9\-_.]+)$/);
 947            if (goalMatch && goalMatch[1].length > 1 && !goalMatch[1].includes('.')) {
 948                const goal = goalMatch[1].trim();
 949                const description = `Run ${goal} goal`;
 950                goals.push({ goal, description });
 951                console.log(`[Maven Plugin Discovery] Found goal (pattern 3): ${goal} - ${description}`);
 952                continue;
 953            }
 954
 955            // Pattern 4: Mojo entries "goal (implementation: ClassName)"
 956            goalMatch = trimmedLine.match(/^([a-zA-Z0-9\-_.]+)\s*\(implementation:\s*[^)]+\)$/);
 957            if (goalMatch) {
 958                const goal = goalMatch[1].trim();
 959                const description = `Run ${goal} goal`;
 960                goals.push({ goal, description });
 961                console.log(`[Maven Plugin Discovery] Found goal (pattern 4): ${goal} - ${description}`);
 962                continue;
 963            }
 964
 965            // Stop if we hit obvious end markers
 966            if (trimmedLine.includes('===') ||
 967                trimmedLine.toLowerCase().includes('for more information') ||
 968                trimmedLine.startsWith('[INFO]') ||
 969                trimmedLine.startsWith('[ERROR]')) {
 970                console.log(`[Maven Plugin Discovery] Stopping parsing at line ${i}: "${trimmedLine}"`);
 971                break;
 972            }
 973        }
 974    }
 975
 976    console.log(`[Maven Plugin Discovery] Parsing complete - foundGoalsHeader: ${foundGoalsHeader}, goals found: ${goals.length}`);
 977
 978    // If we didn't find any goals with the header approach, try a more aggressive search
 979    if (goals.length === 0) {
 980        console.log(`[Maven Plugin Discovery] No goals found with header approach, trying alternative parsing`);
 981        for (const line of lines) {
 982            const trimmedLine = line.trim();
 983            if (trimmedLine.includes(':') && trimmedLine.includes('goal')) {
 984                const parts = trimmedLine.split(':');
 985                if (parts.length >= 4) {
 986                    const goal = parts[3].split(/\s+/)[0];
 987                    if (goal && goal.length > 1) {
 988                        goals.push({ goal, description: `Run ${goal} goal` });
 989                        console.log(`[Maven Plugin Discovery] Found goal (alternative): ${goal}`);
 990                    }
 991                }
 992            }
 993        }
 994    }
 995
 996    return goals;
 997}
