1154        }
1155    }
1156
1157    // Add modules as implicit dependencies (for aggregator projects)
1158    if (projectInfo.modules) {
1159        const projectRoot = dirname(pomFilePath);
1160        for (const module of projectInfo.modules) {
1161            const modulePath = join(projectRoot, module, 'pom.xml');
1162            const moduleProject = mavenReport.projects.get(modulePath);
1163            if (moduleProject) {
1164                const moduleProjectName = `${moduleProject.groupId ?? moduleProject.parent?.groupId}.${moduleProject.artifactId}`;
1165                implicitDeps.push(moduleProjectName);
