1265function collectUniquePlugins(mavenReport: MavenReport, pomFiles: string[]): Set<string> {
1266    const uniquePlugins = new Set<string>();
1267    let totalPluginCount = 0;
1268    let knownPluginCount = 0;
1269    let skippedPluginCount = 0;
1270
1271    for (const pomFile of pomFiles) {
1272        const projectInfo = mavenReport.projects.get(pomFile);
1273        if (!projectInfo?.plugins) continue;
1274
1275        for (const plugin of projectInfo.plugins) {
1276            totalPluginCount++;
1277            const pluginKey = `${plugin.groupId}:${plugin.artifactId}`;
1278
1279            if (PLUGIN_CONFIGS.has(pluginKey)) {
1280                knownPluginCount++;
1281            } else if (SKIP_DISCOVERY_PLUGINS.has(pluginKey)) {
1282                skippedPluginCount++;
1283            } else {
1284                // Only collect unknown plugins that are worth discovering
1285                uniquePlugins.add(pluginKey);
1286            }
1287        }
1288    }
1289
1290    console.log(`[Maven Plugin] Plugin summary: ${totalPluginCount} total, ${knownPluginCount} known, ${skippedPluginCount} skipped, ${uniquePlugins.size} unique unknown plugins to discover`);
1291    console.log(`[Maven Plugin] Unknown plugins:`, Array.from(uniquePlugins).join(', '));
1292
1293    return uniquePlugins;
1294}
1295
1296async function batchDiscoverPlugins(
1297    uniquePlugins: Set<string>,
1298    workspaceRoot: string
1299): Promise<Map<string, DiscoveredPlugin>> {
1300    const discoveredPlugins = new Map<string, DiscoveredPlugin>();
1301
1302    if (uniquePlugins.size === 0) {
1303        console.log(`[Maven Plugin] No unknown plugins to discover`);
1304        return discoveredPlugins;
1305    }
1306
1307    console.log(`[Maven Plugin] Starting parallel discovery of ${uniquePlugins.size} unique plugins...`);
1308    const startTime = Date.now();
1309
1310    // Discover all plugins in parallel using Promise.allSettled
1311    const pluginArray = Array.from(uniquePlugins);
1312    const discoveryPromises = pluginArray.map(async (pluginKey) => {
1313        const discovered = await pluginDiscoveryPool.discover(pluginKey, workspaceRoot);
1314        return { pluginKey, discovered };
1315    });
1316
1317    const results = await Promise.allSettled(discoveryPromises);
1318
1319    // Process results
1320    for (const result of results) {
1321        if (result.status === 'fulfilled' && result.value.discovered) {
1322            discoveredPlugins.set(result.value.pluginKey, result.value.discovered);
1323        }
1324    }
1325
1326    const discoveryTime = Date.now() - startTime;
1327    console.log(`[Maven Plugin] Discovered ${discoveredPlugins.size}/${uniquePlugins.size} plugins in ${discoveryTime}ms`);
1328
1329    return discoveredPlugins;
1330}
1331
1332async function discoverMavenModules(workspaceRoot: string): Promise<string[]> {
1333    const discoveredPoms: string[] = [];
1334    const processed = new Set<string>();
1335
1336    async function processModule(pomPath: string) {
1337        // Convert to absolute path if not already absolute
1338        const absolutePomPath = pomPath.startsWith('/') || pomPath.includes(':')
1339            ? pomPath
1340            : join(workspaceRoot, pomPath);
1341
1342        console.log(`[Maven Plugin] Processing module: ${pomPath} -> ${absolutePomPath}`);
1343
1344        if (processed.has(absolutePomPath)) {
1345            console.log(`[Maven Plugin] Already processed: ${absolutePomPath}`);
1346            return;
1347        }
1348
1349        if (!existsSync(absolutePomPath)) {
1350            console.log(`[Maven Plugin] File does not exist: ${absolutePomPath}`);
1351            return;
1352        }
1353
1354        processed.add(absolutePomPath);
1355        discoveredPoms.push(absolutePomPath);
1356
1357        try {
1358            // Parse this pom.xml to get its modules (use raw POM for module discovery)
1359            const miniReport = await parsePomXml(workspaceRoot, [absolutePomPath], false);
1360            const projectInfo = miniReport.projects.get(absolutePomPath);
1361
1362            if (projectInfo?.modules) {
1363                const currentDir = dirname(absolutePomPath);
1364                console.log(`[Maven Plugin] Found ${projectInfo.modules.length} modules in ${absolutePomPath}`);
1365
1366                // Process each module recursively
1367                for (const module of projectInfo.modules) {
1368                    const modulePomPath = join(currentDir, module, 'pom.xml');
1369                    await processModule(modulePomPath);
1370                }
1371            }
1372        } catch (error: any) {
1373            console.warn(`[Maven Plugin] Error processing ${absolutePomPath}:`, error?.message);
1374        }
1375    }
1376
1377    // Start with root pom.xml
1378    const rootPomPath = join(workspaceRoot, 'pom.xml');
1379    console.log(`[Maven Plugin] Starting module discovery from ${rootPomPath}`);
1380
1381    await processModule(rootPomPath);
1382
1383    console.log(`[Maven Plugin] Module discovery complete. Found ${discoveredPoms.length} Maven projects`);
1384    return discoveredPoms;
1385}
1386
1387export {writeTargetsToCache};
1388

