147/**
148 * Generate Nx configuration by calling the Java Maven analyzer (async version)
149 */
150async function generateNxConfigFromMavenAsync(
151  pomPath: string,
152  options: MavenPluginOptions,
153  context: CreateNodesContextV2
154): Promise<any> {
155  const javaAnalyzerPath = findJavaAnalyzer();
156  if (!javaAnalyzerPath) {
157    throw new Error('Maven analyzer Java program not found. Please ensure maven-script is compiled.');
158  }
159
160  try {
161    // Use Maven exec to run the Java program with proper classpath
162    const mavenScriptDir = javaAnalyzerPath.endsWith('.jar')
163      ? dirname(javaAnalyzerPath)
164      : dirname(dirname(javaAnalyzerPath)); // target/classes -> project root
165
166    // Convert relative path to absolute path
167    const absolutePomPath = pomPath.startsWith('/') ? pomPath : join(workspaceRoot, pomPath);
168    
169    console.log(`Analyzing ${absolutePomPath} using Maven script in ${mavenScriptDir}`);
170
171    const command = `${options.mavenExecutable} exec:java -Dexec.mainClass="MavenModelReader" -Dexec.args="${absolutePomPath} --nx" -q`;
172    const { stdout, stderr } = await execAsync(command, {
173      cwd: mavenScriptDir,
174      timeout: 30000, // 30 second timeout
175      maxBuffer: 1024 * 1024 * 10, // 10MB buffer for large outputs
176    });
177
178    if (stderr && stderr.trim()) {
179      console.warn(`Maven analyzer stderr: ${stderr}`);
180    }
181
182    // Parse JSON output from the Java program
183    const output = stdout;
184    const jsonStart = output.indexOf('{');
185    const jsonEnd = output.lastIndexOf('}') + 1;
186
187    if (jsonStart === -1 || jsonEnd === 0) {
188      throw new Error('No valid JSON output from Maven analyzer');
189    }
190
191    const jsonOutput = output.substring(jsonStart, jsonEnd);
192    return JSON.parse(jsonOutput);
193
194  } catch (error) {
195    throw new Error(`Failed to execute Maven analyzer: ${error.message}`);
196  }
197}
