199/**
200 * Generate Nx configurations for multiple projects using batch processing
201 */
202async function generateBatchNxConfigFromMavenAsync(
203  pomPaths: string[],
204  options: MavenPluginOptions,
205  context: CreateNodesContextV2
206): Promise<Record<string, any>> {
207  const javaAnalyzerPath = findJavaAnalyzer();
208  if (!javaAnalyzerPath) {
209    throw new Error('Maven analyzer Java program not found. Please ensure maven-script is compiled.');
210  }
211  
212  try {
213    // Use Maven exec to run the Java program with stdin
214    const mavenScriptDir = javaAnalyzerPath.endsWith('.jar') 
215      ? dirname(javaAnalyzerPath) 
216      : dirname(dirname(javaAnalyzerPath)); // target/classes -> project root
217    
218    // Convert relative paths to absolute paths
219    const absolutePomPaths = pomPaths.map(pomPath => 
220      pomPath.startsWith('/') ? pomPath : join(workspaceRoot, pomPath)
221    );
222    
223    console.log(`Processing ${absolutePomPaths.length} Maven projects...`);
224    
225    // Create unique output file name to avoid conflicts
226    const outputFile = join(workspaceRoot, `maven-results-${Date.now()}.json`);
227    
228    const { stdout, stderr } = await new Promise<{stdout: string, stderr: string}>((resolve, reject) => {
229      const child = spawn(options.mavenExecutable, [
230        'exec:java',
231        '-Dexec.mainClass=MavenModelReader',
232        '-Dexec.args=--hierarchical --nx',
233        `-Dmaven.output.file=${outputFile}`,
234        `-Duser.dir=${workspaceRoot}`,
235        '-q'
236      ], {
237        cwd: mavenScriptDir,
238        stdio: ['pipe', 'pipe', 'pipe']
239      });
240
241      let stdout = '';
242      let stderr = '';
243
244      child.stdout?.on('data', (data) => {
245        stdout += data.toString();
246      });
247
248      child.stderr?.on('data', (data) => {
249        const text = data.toString();
250        stderr += text;
251        // Only show important logs, not debug spam
252        if (text.includes('ERROR:') || text.includes('Final results')) {
253          console.log(`[Java] ${text.trim()}`);
254        }
255      });
256
257      child.on('close', (code) => {
258        if (code === 0) {
259          resolve({ stdout, stderr });
260        } else {
261          reject(new Error(`Maven process exited with code ${code}. stderr: ${stderr}`));
262        }
263      });
264
265      child.on('error', (error) => {
266        reject(new Error(`Failed to spawn Maven process: ${error.message}`));
267      });
268
269      // No stdin input needed for hierarchical traversal
270      child.stdin?.end();
271
272      // Shorter timeout for responsiveness
273      setTimeout(() => {
274        child.kill();
275        reject(new Error('Maven process timed out after 1 minute'));
276      }, 60000);
277    });
278    
279    if (stderr && stderr.trim()) {
280      console.warn(`Maven analyzer stderr: ${stderr}`);
281    }
282    
283    // Check if process completed successfully
284    if (!stdout.includes('SUCCESS:')) {
285      throw new Error('Maven analyzer did not complete successfully');
286    }
287    
288    // Read JSON output from the file
289    try {
290      const jsonContent = readFileSync(outputFile, 'utf8');
291      
292      // Clean up the temp file
293      try {
294        unlinkSync(outputFile);
295      } catch (e) {
296        console.warn(`Could not delete temp file ${outputFile}: ${e.message}`);
297      }
298      
299      const result = JSON.parse(jsonContent);
300      
301      // Check for errors in the result
302      if (result._errors && result._errors.length > 0) {
303        console.warn(`Maven analyzer encountered ${result._errors.length} errors:`);
304        result._errors.forEach((error: string, index: number) => {
305          console.warn(`  ${index + 1}. ${error}`);
306        });
307      }
308      
309      // Log statistics
310      if (result._stats) {
311        console.log(`Maven analysis stats: ${result._stats.successful}/${result._stats.processed} projects processed successfully (${result._stats.errors} errors)`);
312      }
313      
314      // Remove metadata fields from result before returning
315      delete result._errors;
316      delete result._stats;
317      
318      return result;
319    } catch (error) {
320      throw new Error(`Failed to read output file ${outputFile}: ${error.message}`);
321    }
322    
323  } catch (error) {
324    throw new Error(`Failed to execute Maven analyzer in batch mode: ${error.message}`);
325  }
326}
