334  // If it's a JAR file, just return it
335  if (javaAnalyzerPath.endsWith('.jar')) {
336    return javaAnalyzerPath;
337  }
338
339  // For compiled classes, we need to include Maven dependencies
340  const mavenScriptDir = dirname(javaAnalyzerPath); // target/classes -> target
341  const projectDir = dirname(mavenScriptDir);       // target -> project root
342
343  try {
344    // Use Maven to build dependency classpath
345    const command = 'mvn dependency:build-classpath -q -Dmdep.outputFile=/dev/stdout';
346    const dependencyClasspath = execSync(command, {
347      cwd: projectDir,
348      encoding: 'utf8',
349      timeout: 30000,
350    }).trim();
351
352    if (dependencyClasspath) {
353      classpathParts.push(dependencyClasspath);
354    }
355  } catch (error) {
356    console.warn('Failed to build Maven dependency classpath, using basic classpath');
357  }
