421    const dirName = projectPath.split('/').pop();
422    if (dirName === artifactId) {
423      return dirName;
424    }
425  }
426
427  // Try to find by parsing pom.xml artifactId for more accurate matching
428  for (const projectPath of mavenProjects) {
