485/**
486 * Normalize target configurations from Maven analysis and add Maven lifecycle phases
487 */
488function normalizeTargets(targets: Record<string, any>, nxConfig: any, options: MavenPluginOptions): Record<string, TargetConfiguration> {
489  const normalizedTargets: Record<string, TargetConfiguration> = {};
490
491  // First add any existing targets from Maven analysis
492  for (const [name, target] of Object.entries(targets)) {
493    // Map common Maven targets to Nx conventions
494    let targetName = name;
495    if (name === 'package' && options.buildTargetName !== 'package') {
496      targetName = options.buildTargetName!;
497    } else if (name === 'test' && options.testTargetName !== 'test') {
498      targetName = options.testTargetName!;
499    } else if (name === 'serve' && options.serveTargetName !== 'serve') {
500      targetName = options.serveTargetName!;
501    }
502
503    normalizedTargets[targetName] = {
504      executor: target.executor || '@nx/run-commands:run-commands',
505      options: target.options || {},
506      inputs: target.inputs,
507      outputs: target.outputs,
508      dependsOn: target.dependsOn,
509      configurations: target.configurations,
510    };
511  }
512
513  // Add Maven lifecycle phase targets and plugin goals from detected data
514  const detectedTargets = generateDetectedTargets(nxConfig, options);
515  Object.assign(normalizedTargets, detectedTargets);
516
517  return normalizedTargets;
518}
