520/**
521 * Generate targets based on pre-calculated data from Java analyzer
522 * Uses goalsByPhase and goalDependencies from Java for optimal structure
523 */
524function generateDetectedTargets(nxConfig: any, options: MavenPluginOptions): Record<string, TargetConfiguration> {
525  const detectedTargets: Record<string, TargetConfiguration> = {};
526  
527  // Get pre-calculated data from Java analyzer
528  const relevantPhases = nxConfig.relevantPhases || [];
529  const pluginGoals = nxConfig.pluginGoals || [];
530  const goalsByPhase = nxConfig.goalsByPhase || {};
531  const goalDependencies = nxConfig.goalDependencies || {};
532  const crossProjectDependencies = nxConfig.crossProjectDependencies || {};
533  
534  // Step 1: Generate goal targets using pre-calculated dependencies
535  for (const goalInfo of pluginGoals) {
536    if (goalInfo.targetName && !detectedTargets[goalInfo.targetName]) {
537      const goalTarget = createGoalTarget(goalInfo, options, goalDependencies, crossProjectDependencies);
538      if (goalTarget) {
539        detectedTargets[goalInfo.targetName] = goalTarget;
540      }
541    }
542  }
543  
544  // Step 2: Generate phase targets that depend on their own goals (from Java)
545  for (const phase of relevantPhases) {
546    const phaseTarget = createPhaseTarget(phase, options, goalsByPhase);
547    if (phaseTarget) {
548      detectedTargets[phase] = phaseTarget;
549    }
550  }
551  
552  return detectedTargets;
553}
