 554            if (projectRoot.startsWith("/")) {
 555                String workspaceRoot = System.getProperty("user.dir");
 556                if (projectRoot.startsWith(workspaceRoot + "/")) {
 557                    projectRoot = projectRoot.substring(workspaceRoot.length() + 1);
 558                }
 559            }
 560            
 561            // Filter internal dependencies to only include discovered projects
 562            List<String> internalDeps = new ArrayList<>();
 563            if (model.getDependencies() != null) {
 564                for (Dependency dep : model.getDependencies()) {
 565                    if (isInternalDependency(dep.getGroupId(), dep.getArtifactId())) {
 566                        String depName = dep.getGroupId() + ":" + dep.getArtifactId();
 567                        // Only include if the dependency is in our discovered projects list
 568                        if (discoveredProjects.contains(depName)) {
 569                            internalDeps.add(depName);
 570                        }
 571                    }
 572                }
 573            }
 574            
 575            // Detect relevant phases and plugin goals
 576            System.err.println("DEBUG: About to detect phases for " + projectName);
 577            List<String> relevantPhases = detectRelevantPhases(model);
 578            System.err.println("DEBUG: Found " + relevantPhases.size() + " phases: " + relevantPhases);
 579            List<Map<String, Object>> pluginGoals = detectPluginGoals(model);
 580            System.err.println("DEBUG: Found " + pluginGoals.size() + " plugin goals");
 581            
 582            // Detect phase dependencies using Maven lifecycle information
 583            Map<String, List<String>> phaseDependencies = detectPhaseDependencies(relevantPhases);
 584            System.err.println("DEBUG: Detected phase dependencies for " + phaseDependencies.size() + " phases");
 585            
 586            // Detect cross-project target dependencies  
 587            Map<String, List<String>> crossProjectDependencies = detectCrossProjectTargetDependencies(internalDeps, relevantPhases, pluginGoals);
 588            System.err.println("DEBUG: Detected cross-project dependencies for " + crossProjectDependencies.size() + " targets");
 589            
 590            // NEW: Organize goals by phase for better task graph structure
 591            // First pass: organize goals and identify missing phases
 592            Map<String, List<String>> goalsByPhase = organizeGoalsByPhase(pluginGoals, relevantPhases);
 593            System.err.println("DEBUG: Organized goals by phase for " + goalsByPhase.size() + " phases");
 594            
 595            // Add missing phases that goals need (like test-compile)
 596            addMissingPhasesForGoals(pluginGoals, relevantPhases, goalsByPhase);
 597            
 598            // Regenerate phase dependencies with new phases
 599            Map<String, List<String>> updatedPhaseDependencies = detectPhaseDependencies(relevantPhases);
 600            
 601            // NEW: Generate goal-to-goal dependencies (goals depend on goals from prerequisite phases)
 602            Map<String, List<String>> goalDependencies = generateGoalDependencies(pluginGoals, goalsByPhase, updatedPhaseDependencies, relevantPhases);
 603            System.err.println("DEBUG: Generated goal dependencies for " + goalDependencies.size() + " goals");
 604            
 605            // Write to file
 606            if (!isFirst) {
 607                writer.write(",\n");
 608            }
 609            
 610            writer.write("  \"" + escapeJsonString(projectRoot) + "\": {\n");
 611            writer.write("    \"name\": \"" + escapeJsonString(projectName) + "\",\n");
 612            writer.write("    \"projectType\": \"library\",\n");
 613            writer.write("    \"implicitDependencies\": {\n");
 614            writer.write("      \"projects\": [");
 615            for (int i = 0; i < internalDeps.size(); i++) {
 616                if (i > 0) writer.write(", ");
 617                writer.write("\"" + escapeJsonString(internalDeps.get(i)) + "\"");
 618            }
 619            writer.write("]\n");
 620            writer.write("    },\n");
 621            
 622            // Add relevant phases
 623            writer.write("    \"relevantPhases\": [");
 624            for (int i = 0; i < relevantPhases.size(); i++) {
 625                if (i > 0) writer.write(", ");
 626                writer.write("\"" + escapeJsonString(relevantPhases.get(i)) + "\"");
 627            }
 628            writer.write("],\n");
 629            
 630            // Add plugin goals
 631            writer.write("    \"pluginGoals\": [\n");
 632            for (int i = 0; i < pluginGoals.size(); i++) {
 633                if (i > 0) writer.write(",\n");
 634                writer.write("      " + mapToJson(pluginGoals.get(i)));
 635            }
 636            writer.write("\n    ],\n");
 637            
 638            // Add phase dependencies (use updated ones)
 639            Map<String, Object> phaseDepsAsObjects = new LinkedHashMap<>();
 640            for (Map.Entry<String, List<String>> entry : updatedPhaseDependencies.entrySet()) {
 641                phaseDepsAsObjects.put(entry.getKey(), entry.getValue());
 642            }
 643            writer.write("    \"phaseDependencies\": " + mapToJson(phaseDepsAsObjects) + ",\n");
 644            
 645            // Add cross-project target dependencies
 646            Map<String, Object> crossProjectDepsAsObjects = new LinkedHashMap<>();
 647            for (Map.Entry<String, List<String>> entry : crossProjectDependencies.entrySet()) {
 648                crossProjectDepsAsObjects.put(entry.getKey(), entry.getValue());
 649            }
 650            writer.write("    \"crossProjectDependencies\": " + mapToJson(crossProjectDepsAsObjects) + ",\n");
 651            
 652            // Add goals organized by phase
 653            Map<String, Object> goalsByPhaseAsObjects = new LinkedHashMap<>();
 654            for (Map.Entry<String, List<String>> entry : goalsByPhase.entrySet()) {
 655                goalsByPhaseAsObjects.put(entry.getKey(), entry.getValue());
 656            }
 657            writer.write("    \"goalsByPhase\": " + mapToJson(goalsByPhaseAsObjects) + ",\n");
 658            
 659            // Add goal-to-goal dependencies  
 660            Map<String, Object> goalDepsAsObjects = new LinkedHashMap<>();
 661            for (Map.Entry<String, List<String>> entry : goalDependencies.entrySet()) {
 662                goalDepsAsObjects.put(entry.getKey(), entry.getValue());
 663            }
 664            writer.write("    \"goalDependencies\": " + mapToJson(goalDepsAsObjects) + "\n");
 665            writer.write("  }");
 666            
 667            // Only log every 25 projects to reduce output
 668            if (processed % 25 == 0 || processed == 1) {
 669                System.err.println("DEBUG: Processed " + projectName + " with " + internalDeps.size() + " filtered dependencies (" + processed + " total)");
 670            }
 671            
 672        } finally {
 673            // Explicitly clear references
 674            model = null;
 675        }
 676    }
