 689    /**
 690     * Read effective POM using Maven ModelBuilder API with full inheritance and interpolation
 691     */
 692    private static Model readEffectivePomFile(String pomPath) throws Exception {
 693        // Create ModelBuilder
 694        ModelBuilder modelBuilder = new DefaultModelBuilderFactory().newInstance();
 695        
 696        // Create model building request with minimal validation for now
 697        DefaultModelBuildingRequest request = new DefaultModelBuildingRequest();
 698        request.setPomFile(new File(pomPath));
 699        request.setValidationLevel(ModelBuildingRequest.VALIDATION_LEVEL_MINIMAL);
 700        request.setProcessPlugins(true);
 701        request.setTwoPhaseBuilding(false);
 702        
 703        // For now, try without ModelResolver to see basic functionality
 704        // This will work for POMs without parent references or imports
 705        try {
 706            ModelBuildingResult result = modelBuilder.build(request);
 707            return result.getEffectiveModel();
 708        } catch (Exception e) {
 709            // If that fails, try with a basic model resolver
 710            RepositorySystem repositorySystem = createRepositorySystem();
 711            RepositorySystemSession repositorySession = createRepositorySession(repositorySystem);
 712            ModelResolver modelResolver = createModelResolver(repositorySystem, repositorySession);
 713            
 714            request.setModelResolver(modelResolver);
 715            ModelBuildingResult result = modelBuilder.build(request);
 716            return result.getEffectiveModel();
 717        }
 718    }
