 746    /**
 747     * Create model resolver for resolving parent POMs and imports
 748     */
 749    private static ModelResolver createModelResolver(RepositorySystem repositorySystem, 
 750                                                   RepositorySystemSession repositorySession) {
 751        // Define remote repositories (Maven Central, etc.)
 752        List<RemoteRepository> repositories = new ArrayList<>();
 753        repositories.add(new RemoteRepository.Builder("central", "default", "https://repo1.maven.org/maven2/").build());
 754        
 755        // Try multiple constructor signatures for different Maven versions
 756        try {
 757            Class<?> resolverClass = Class.forName("org.apache.maven.repository.internal.DefaultModelResolver");
 758            
 759            // Try constructor with RepositorySystemSession, RequestTrace, String, ArtifactResolver, RemoteRepositoryManager, List
 760            try {
 761                Class<?> requestTraceClass = Class.forName("org.eclipse.aether.RequestTrace");
 762                Class<?> artifactResolverClass = Class.forName("org.eclipse.aether.resolution.ArtifactResolver"); 
 763                Class<?> remoteRepoManagerClass = Class.forName("org.eclipse.aether.impl.RemoteRepositoryManager");
 764                
 765                java.lang.reflect.Constructor<?> constructor = resolverClass.getDeclaredConstructor(
 766                    RepositorySystemSession.class,
 767                    requestTraceClass,
 768                    String.class,
 769                    artifactResolverClass,
 770                    remoteRepoManagerClass,
 771                    List.class
 772                );
 773                constructor.setAccessible(true);
 774                
 775                // Get components from RepositorySystem
 776                Object artifactResolver = repositorySystem.getClass().getMethod("getService", Class.class)
 777                    .invoke(repositorySystem, artifactResolverClass);
 778                Object remoteRepoManager = repositorySystem.getClass().getMethod("getService", Class.class)
 779                    .invoke(repositorySystem, remoteRepoManagerClass);
 780                    
 781                return (ModelResolver) constructor.newInstance(
 782                    repositorySession, null, "project", artifactResolver, remoteRepoManager, repositories);
 783                    
 784            } catch (Exception e1) {
 785                // Try simpler constructor signature 
 786                try {
 787                    java.lang.reflect.Constructor<?> constructor = resolverClass.getDeclaredConstructor(
 788                        RepositorySystemSession.class,
 789                        List.class
 790                    );
 791                    constructor.setAccessible(true);
 792                    return (ModelResolver) constructor.newInstance(repositorySession, repositories);
 793                } catch (Exception e2) {
 794                    throw new RuntimeException("No compatible DefaultModelResolver constructor found. Tried multiple signatures.", e2);
 795                }
 796            }
 797            
 798        } catch (ClassNotFoundException e) {
 799            throw new RuntimeException("DefaultModelResolver class not found: " + e.getMessage(), e);
 800        }
 801    }
