 861    private static boolean isInternalDependency(String groupId, String artifactId) {
 862        if (groupId == null || artifactId == null) {
 863            return false;
 864        }
 865        
 866        // More conservative check for Quarkus internal dependencies
 867        // Only include artifacts that are likely to be in the workspace
 868        if (!groupId.equals("io.quarkus")) {
 869            return false;
 870        }
 871        
 872        // Exclude known external dependencies that are not part of the workspace
 873        if (artifactId.equals("quarkus-fs-util") || 
 874            artifactId.equals("quarkus-spring-context-api") ||
 875            artifactId.equals("quarkus-spring-data-rest-api") ||
 876            artifactId.contains("-api") && !artifactId.endsWith("-deployment")) {
 877            return false;
 878        }
 879        
 880        return artifactId.startsWith("quarkus-") || artifactId.equals("arc");
 881    }
