 883    /**
 884     * Detect relevant Maven phases for a project based on packaging, plugins, and framework
 885     */
 886    private static List<String> detectRelevantPhases(Model model) {
 887        List<String> phases = new ArrayList<>();
 888        String packaging = model.getPackaging();
 889        if (packaging == null) {
 890            packaging = "jar"; // Default packaging
 891        }
 892        
 893        // Add standard phases based on packaging type
 894        phases.addAll(getPackagingSpecificPhases(packaging));
 895        
 896        // Add phases from plugin executions
 897        phases.addAll(getPluginBoundPhases(model));
 898        
 899        // Add framework-specific phases
 900        phases.addAll(detectFrameworkPhases(model));
 901        
 902        // Remove duplicates and sort
 903        List<String> uniquePhases = new ArrayList<>();
 904        for (String phase : phases) {
 905            if (!uniquePhases.contains(phase)) {
 906                uniquePhases.add(phase);
 907            }
 908        }
 909        
 910        return uniquePhases;
 911    }
