 913    /**
 914     * Get standard phases for different packaging types
 915     */
 916    private static List<String> getPackagingSpecificPhases(String packaging) {
 917        List<String> phases = new ArrayList<>();
 918        
 919        // Common phases for all packaging types
 920        phases.add("clean");
 921        phases.add("validate");
 922        phases.add("compile");
 923        
 924        switch (packaging.toLowerCase()) {
 925            case "pom":
 926                // Parent POMs typically only need basic phases
 927                phases.add("install");
 928                phases.add("deploy");
 929                break;
 930            case "jar":
 931            case "bundle": // OSGi bundles
 932                phases.add("test-compile");
 933                phases.add("test");
 934                phases.add("package");
 935                phases.add("verify");
 936                phases.add("install");
 937                phases.add("deploy");
 938                break;
 939            case "war":
 940                phases.add("process-resources");
 941                phases.add("process-classes");
 942                phases.add("test-compile");
 943                phases.add("test");
 944                phases.add("package");
 945                phases.add("verify");
 946                phases.add("install");
 947                phases.add("deploy");
 948                break;
 949            case "maven-plugin":
 950                phases.add("process-classes");
 951                phases.add("test-compile");
 952                phases.add("test");
 953                phases.add("package");
 954                phases.add("verify");
 955                phases.add("install");
 956                phases.add("deploy");
 957                break;
 958            default:
 959                // For unknown packaging, include common phases
 960                phases.add("test");
 961                phases.add("package");
 962                phases.add("install");
 963                break;
 964        }
 965        
 966        return phases;
 967    }
