 969    /**
 970     * Detect phases that have explicit plugin bindings
 971     */
 972    private static List<String> getPluginBoundPhases(Model model) {
 973        List<String> phases = new ArrayList<>();
 974        
 975        if (model.getBuild() != null && model.getBuild().getPlugins() != null) {
 976            for (Plugin plugin : model.getBuild().getPlugins()) {
 977                if (plugin.getExecutions() != null) {
 978                    for (PluginExecution execution : plugin.getExecutions()) {
 979                        String phase = execution.getPhase();
 980                        if (phase != null && !phase.trim().isEmpty()) {
 981                            phases.add(phase.trim());
 982                        }
 983                    }
 984                }
 985            }
 986        }
 987        
 988        return phases;
 989    }
