1112        standardLifecycle.put("package", new ArrayList<String>() {{ add("prepare-package"); }});
1113        standardLifecycle.put("pre-integration-test", new ArrayList<String>() {{ add("package"); }});
1114        standardLifecycle.put("integration-test", new ArrayList<String>() {{ add("pre-integration-test"); }});
