1132        standardLifecycle.put("generate-code", new ArrayList<String>() {{ add("validate"); }});
1133        standardLifecycle.put("quarkus:dev", new ArrayList<String>() {{ add("compile"); }});
1134        standardLifecycle.put("quarkus:build", new ArrayList<String>() {{ add("test"); }});
1135        standardLifecycle.put("spring-boot:run", new ArrayList<String>() {{ add("compile"); }});
1136        standardLifecycle.put("spring-boot:build-image", new ArrayList<String>() {{ add("package"); }});
1137        
1138        // Include ALL standard Maven phases in dependencies, not just relevant ones
1139        // This allows recursive goal dependency traversal to work through the full lifecycle
1140        for (Map.Entry<String, List<String>> entry : standardLifecycle.entrySet()) {
1141            dependencies.put(entry.getKey(), new ArrayList<>(entry.getValue()));
1142        }
1143        
1144        // Then add any relevant phases that might not be in the standard lifecycle
1145        for (String phase : relevantPhases) {
1146            if (!dependencies.containsKey(phase)) {
1147                dependencies.put(phase, new ArrayList<>());
1148            }
1149        }
1150        
1151        return dependencies;
1152    }
