1154    /**
1155     * Detect cross-project target dependencies based on Maven dependencies
1156     */
1157    private static Map<String, List<String>> detectCrossProjectTargetDependencies(
1158            List<String> internalDeps, List<String> relevantPhases, List<Map<String, Object>> pluginGoals) {
1159        
1160        Map<String, List<String>> crossProjectDeps = new LinkedHashMap<>();
1161        
1162        if (internalDeps.isEmpty()) {
1163            return crossProjectDeps;
1164        }
1165        
1166        // For each target (phase or goal), determine what dependency project targets it should wait for
1167        
1168        // Phase target dependencies
1169        for (String phase : relevantPhases) {
1170            List<String> targetDeps = new ArrayList<>();
1171            
1172            switch (phase) {
1173                case "compile":
1174                case "test-compile":
1175                    // Compilation phases should wait for dependency projects to be compiled
1176                    // Use fallback to 'validate' if 'compile' doesn't exist
1177                    for (String dep : internalDeps) {
1178                        targetDeps.add(dep + ":compile|validate");
1179                    }
1180                    break;
1181                case "test":
1182                    // Test phase should wait for dependency projects to be compiled
1183                    // Use fallback to 'validate' if 'compile' doesn't exist
1184                    for (String dep : internalDeps) {
1185                        targetDeps.add(dep + ":compile|validate");
1186                    }
1187                    break;
1188                case "package":
1189                case "verify":
1190                case "install":
1191                case "deploy":
1192                    // Packaging/deployment phases should wait for dependency projects to be packaged
1193                    // Use fallback chain: package -> compile -> validate
1194                    for (String dep : internalDeps) {
1195                        targetDeps.add(dep + ":package|compile|validate");
1196                    }
1197                    break;
1198                case "integration-test":
1199                    // Integration tests need dependency projects to be packaged
1200                    // Use fallback chain: package -> compile -> validate
1201                    for (String dep : internalDeps) {
1202                        targetDeps.add(dep + ":package|compile|validate");
1203                    }
1204                    break;
1205                // For other phases like clean, validate, process-resources, etc., usually no cross-project deps needed
1206            }
1207            
1208            if (!targetDeps.isEmpty()) {
1209                crossProjectDeps.put(phase, targetDeps);
1210            }
1211        }
1212        
1213        // Plugin goal dependencies
1214        for (Map<String, Object> goalInfo : pluginGoals) {
1215            String targetName = (String) goalInfo.get("targetName");
1216            String targetType = (String) goalInfo.get("targetType");
1217            
1218            if (targetName != null && targetType != null) {
1219                List<String> targetDeps = new ArrayList<>();
1220                
1221                switch (targetType) {
1222                    case "serve":
1223                        // Serve goals (like quarkus:dev) need dependency projects compiled
1224                        // Use fallback to 'validate' if 'compile' doesn't exist
1225                        for (String dep : internalDeps) {
1226                            targetDeps.add(dep + ":compile|validate");
1227                        }
1228                        break;
1229                    case "build":
1230                        // Build goals need dependency projects packaged
1231                        // Use fallback chain: package -> compile -> validate
1232                        for (String dep : internalDeps) {
1233                            targetDeps.add(dep + ":package|compile|validate");
1234                        }
1235                        break;
1236                    case "test":
1237                        // Test goals need dependency projects compiled
1238                        // Use fallback to 'validate' if 'compile' doesn't exist
1239                        for (String dep : internalDeps) {
1240                            targetDeps.add(dep + ":compile|validate");
1241                        }
1242                        break;
1243                    case "deploy":
1244                        // Deploy goals need dependency projects packaged
1245                        // Use fallback chain: package -> compile -> validate
1246                        for (String dep : internalDeps) {
1247                            targetDeps.add(dep + ":package|compile|validate");
1248                        }
1249                        break;
1250                }
1251                
1252                if (!targetDeps.isEmpty()) {
1253                    crossProjectDeps.put(targetName, targetDeps);
1254                }
1255            }
1256        }
1257        
1258        return crossProjectDeps;
1259    }
