1261    /**
1262     * Detect plugin goals that would be useful as NX targets
1263     */
1264    private static List<Map<String, Object>> detectPluginGoals(Model model) {
1265        List<Map<String, Object>> pluginGoals = new ArrayList<>();
1266        
1267        if (model.getBuild() != null && model.getBuild().getPlugins() != null) {
1268            for (Plugin plugin : model.getBuild().getPlugins()) {
1269                String pluginKey = plugin.getGroupId() + ":" + plugin.getArtifactId();
1270                
1271                // Process plugin executions
1272                if (plugin.getExecutions() != null) {
1273                    for (PluginExecution execution : plugin.getExecutions()) {
1274                        List<String> goals = execution.getGoals();
1275                        if (goals != null && !goals.isEmpty()) {
1276                            for (String goal : goals) {
1277                                Map<String, Object> goalInfo = createPluginGoalInfo(
1278                                    plugin, execution, goal, execution.getPhase()
1279                                );
1280                                if (goalInfo != null) {
1281                                    pluginGoals.add(goalInfo);
1282                                }
1283                            }
1284                        }
1285                    }
1286                }
1287                
1288                // Also check for common framework-specific goals even if not in executions
1289                pluginGoals.addAll(detectFrameworkSpecificGoals(plugin));
1290            }
1291        }
1292        
1293        // Also detect framework goals based on dependencies (for inherited plugins)
1294        pluginGoals.addAll(detectFrameworkGoalsByDependencies(model));
1295        
1296        return pluginGoals;
1297    }
