1449    /**
1450     * Get suggested phase dependencies for a plugin goal
1451     */
1452    private static List<String> getSuggestedPhaseDependencies(String pluginKey, String goal, String targetType, String phase) {
1453        List<String> dependencies = new ArrayList<>();
1454        
1455        // Framework-specific dependencies
1456        if (pluginKey.contains("quarkus")) {
1457            switch (goal) {
1458                case "dev":
1459                    dependencies.add("compile");
1460                    break;
1461                case "build":
1462                    dependencies.add("test");
1463                    break;
1464                case "generate-code":
1465                    dependencies.add("validate");
1466                    break;
1467                case "test":
1468                    dependencies.add("test-compile");
1469                    break;
1470            }
1471        } else if (pluginKey.contains("spring-boot")) {
1472            switch (goal) {
1473                case "run":
1474                    dependencies.add("compile");
1475                    break;
1476                case "build-image":
1477                    dependencies.add("package");
1478                    break;
1479                case "repackage":
1480                    dependencies.add("test");
1481                    break;
1482            }
1483        } else {
1484            // Generic dependencies based on target type
1485            switch (targetType) {
1486                case "serve":
1487                    dependencies.add("compile");
1488                    break;
1489                case "build":
1490                    dependencies.add("test");
1491                    break;
1492                case "test":
1493                    dependencies.add("test-compile");
1494                    break;
1495                case "deploy":
1496                    dependencies.add("package");
1497                    break;
1498                case "utility":
1499                    dependencies.add("compile");
1500                    break;
1501            }
1502        }
1503        
1504        // If the goal is explicitly bound to a phase, use that phase's prerequisites instead
1505        if (phase != null && !phase.trim().isEmpty() && !"null".equals(phase)) {
1506            // Use standard Maven lifecycle dependencies for bound goals
1507            switch (phase) {
1508                case "compile":
1509                    dependencies.clear();
1510                    dependencies.add("process-resources");
1511                    break;
1512                case "test":
1513                    dependencies.clear();
1514                    dependencies.add("process-test-classes");
1515                    break;
1516                case "package":
1517                    dependencies.clear();
1518                    dependencies.add("test");
1519                    break;
1520                case "verify":
1521                    dependencies.clear();
1522                    dependencies.add("package");
1523                    break;
1524                case "install":
1525                    dependencies.clear();
1526                    dependencies.add("verify");
1527                    break;
1528                case "deploy":
1529                    dependencies.clear();
1530                    dependencies.add("install");
1531                    break;
1532            }
1533        }
1534        
1535        return dependencies;
1536    }
