1557    /**
1558     * Check if a plugin goal would be useful as an NX target
1559     */
1560    private static boolean isUsefulPluginGoal(String pluginKey, String goal) {
1561        // Skip internal Maven goals that aren't useful for users
1562        if (goal.equals("compile") || goal.equals("testCompile") || 
1563            goal.equals("process-classes") || goal.equals("process-test-classes")) {
1564            return false;
1565        }
1566        
1567        // Include development/serving goals
1568        if (goal.equals("run") || goal.equals("dev") || goal.equals("serve")) {
1569            return true;
1570        }
1571        
1572        // Include build/packaging goals
1573        if (goal.equals("build") || goal.equals("package") || goal.equals("repackage") ||
1574            goal.equals("build-image") || goal.equals("docker-build")) {
1575            return true;
1576        }
1577        
1578        // Include code generation goals
1579        if (goal.equals("generate") || goal.equals("generate-sources") || 
1580            goal.equals("generate-code") || goal.contains("generate")) {
1581            return true;
1582        }
1583        
1584        // Include test goals
1585        if (goal.equals("test") || goal.equals("integration-test") || goal.contains("test")) {
1586            return true;
1587        }
1588        
1589        // Include deployment goals
1590        if (goal.equals("deploy") || goal.equals("push") || goal.contains("deploy")) {
1591            return true;
1592        }
1593        
1594        return false;
1595    }
