1597    /**
1598     * Create a target name from plugin and goal
1599     */
1600    private static String createTargetNameFromGoal(String pluginKey, String goal) {
1601        // Extract short plugin name
1602        String pluginName = "";
1603        if (pluginKey.contains("spring-boot")) {
1604            pluginName = "spring-boot";
1605        } else if (pluginKey.contains("quarkus")) {
1606            pluginName = "quarkus";
1607        } else if (pluginKey.contains("docker")) {
1608            pluginName = "docker";
1609        } else {
1610            // Extract from artifactId
1611            String[] parts = pluginKey.split(":");
1612            if (parts.length > 1) {
1613                pluginName = parts[1].replace("-maven-plugin", "").replace("-plugin", "");
1614            }
1615        }
1616        
1617        // Create target name
1618        if (pluginName.isEmpty()) {
1619            return goal;
1620        } else if (goal.equals("run") || goal.equals("dev")) {
1621            return "serve"; // Map to common NX convention
1622        } else if (goal.equals("build") || goal.equals("package") || goal.equals("repackage")) {
1623            return "build"; // Map to common NX convention
1624        } else {
1625            return pluginName + ":" + goal;
1626        }
1627    }
