1631     */
1632    private static String determineTargetType(String pluginKey, String goal) {
1633        if (goal.equals("run") || goal.equals("dev") || goal.equals("serve")) {
1634            return "serve";
1635        } else if (goal.equals("test") || goal.contains("test")) {
1636            return "test";
1637        } else if (goal.equals("build") || goal.equals("package") || goal.equals("compile") ||
1638                   goal.contains("generate") || goal.equals("repackage")) {
