1669    private static String mapToJson(Map<String, Object> map) {
1670        StringBuilder json = new StringBuilder();
1671        json.append("{");
1672        
1673        boolean first = true;
1674        for (Map.Entry<String, Object> entry : map.entrySet()) {
1675            if (!first) json.append(",");
1676            json.append("\"").append(escapeJsonString(entry.getKey())).append("\":");
1677            json.append(objectToJson(entry.getValue()));
1678            first = false;
1679        }
1680        
1681        json.append("}");
1682        return json.toString();
1683    }
1684    
1685    private static String objectToJson(Object obj) {
1686        if (obj == null) {
1687            return "null";
1688        } else if (obj instanceof String) {
1689            return "\"" + escapeJsonString((String) obj) + "\"";
1690        } else if (obj instanceof Number || obj instanceof Boolean) {
1691            return obj.toString();
1692        } else if (obj instanceof List) {
1693            List<?> list = (List<?>) obj;
1694            StringBuilder json = new StringBuilder("[");
1695            for (int i = 0; i < list.size(); i++) {
1696                if (i > 0) json.append(",");
1697                json.append(objectToJson(list.get(i)));
1698            }
1699            json.append("]");
1700            return json.toString();
1701        } else if (obj instanceof Map) {
1702            return mapToJson((Map<String, Object>) obj);
1703        } else {
1704            return "\"" + escapeJsonString(obj.toString()) + "\"";
1705        }
1706    }
1707    
1708    private static String escapeJsonString(String str) {
1709        if (str == null) return "";
1710        return str.replace("\\", "\\\\")
1711                  .replace("\"", "\\\"")
1712                  .replace("\b", "\\b")
1713                  .replace("\f", "\\f")
1714                  .replace("\n", "\\n")
1715                  .replace("\r", "\\r")
1716                  .replace("\t", "\\t");
1717    }
