1719    /**
1720     * Organize plugin goals by their associated Maven phases
1721     * This determines which phase each goal logically belongs to
1722     */
1723    private static Map<String, List<String>> organizeGoalsByPhase(List<Map<String, Object>> pluginGoals, List<String> relevantPhases) {
1724        Map<String, List<String>> goalsByPhase = new LinkedHashMap<>();
1725        
1726        // Initialize all phases with empty lists
1727        for (String phase : relevantPhases) {
1728            goalsByPhase.put(phase, new ArrayList<>());
1729        }
1730        
1731        // Assign each goal to a phase
1732        for (Map<String, Object> goalInfo : pluginGoals) {
1733            String targetName = (String) goalInfo.get("targetName");
1734            String goalPhase = (String) goalInfo.get("phase");
1735            String goal = (String) goalInfo.get("goal");
1736            String targetType = (String) goalInfo.get("targetType");
1737            String pluginKey = (String) goalInfo.get("pluginKey");
1738            
1739            if (targetName == null) continue;
1740            
1741            String assignedPhase = null;
1742            
1743            // Try explicit phase binding first
1744            if (goalPhase != null && !goalPhase.equals("null") && relevantPhases.contains(goalPhase)) {
1745                assignedPhase = goalPhase;
1746            } else {
1747                // Assign based on target type and goal patterns
1748                if ("build".equals(targetType)) {
1749                    if (goal.contains("testCompile") || goal.equals("testCompile")) {
1750                        assignedPhase = "test-compile";
1751                    } else if (goal.contains("compile") && !goal.contains("test")) {
1752                        assignedPhase = "compile";
1753                    } else if (goal.equals("compile")) {
1754                        assignedPhase = "compile";
1755                    } else if (pluginKey.contains("compiler")) {
1756                        assignedPhase = goal.contains("test") ? "test-compile" : "compile";
1757                    } else {
1758                        assignedPhase = "package"; // Default for build goals
1759                    }
1760                } else if ("test".equals(targetType)) {
1761                    assignedPhase = "test";
1762                } else if ("serve".equals(targetType)) {
1763                    assignedPhase = "compile"; // Dev servers need compiled code
1764                } else if ("deploy".equals(targetType)) {
1765                    assignedPhase = "deploy";
1766                } else if ("utility".equals(targetType)) {
1767                    assignedPhase = "validate";
1768                } else {
1769                    // Infer from goal name
1770                    if (goal.contains("compile")) {
1771                        assignedPhase = goal.contains("test") ? "test-compile" : "compile";
1772                    } else if (goal.contains("test")) {
1773                        assignedPhase = "test";
1774                    } else {
1775                        assignedPhase = "package"; // Default fallback
1776                    }
1777                }
1778            }
1779            
1780            // Ensure assigned phase exists in relevant phases
1781            if (assignedPhase != null && relevantPhases.contains(assignedPhase)) {
1782                goalsByPhase.get(assignedPhase).add(targetName);
1783            } else if (assignedPhase != null && !relevantPhases.isEmpty()) {
1784                // Better fallback logic: find a suitable alternative phase
1785                String fallbackPhase = findBestFallbackPhase(assignedPhase, relevantPhases);
1786                if (fallbackPhase != null) {
1787                    goalsByPhase.get(fallbackPhase).add(targetName);
1788                }
1789                // If no suitable fallback, skip this goal rather than misplace it
1790            }
1791        }
1792        
1793        return goalsByPhase;
1794    }
1795    
1796    /**
1797     * Add missing phases that goals actually need (like test-compile for testCompile goals)
1798     */
1799    private static void addMissingPhasesForGoals(List<Map<String, Object>> pluginGoals, List<String> relevantPhases, Map<String, List<String>> goalsByPhase) {
1800        Set<String> neededPhases = new LinkedHashSet<>();
1801        
1802        // Identify phases that goals actually need
1803        for (Map<String, Object> goalInfo : pluginGoals) {
1804            String goal = (String) goalInfo.get("goal");
1805            String targetType = (String) goalInfo.get("targetType");
1806            String targetName = (String) goalInfo.get("targetName");
1807            
1808            if (targetName == null) continue;
1809            
1810            // Determine ideal phase for this goal
1811            String idealPhase = null;
1812            if ("build".equals(targetType)) {
1813                if (goal.contains("testCompile") || goal.equals("testCompile")) {
1814                    idealPhase = "test-compile";
1815                } else if (goal.contains("compile") && !goal.contains("test")) {
1816                    idealPhase = "compile";
1817                }
1818            } else if ("test".equals(targetType)) {
1819                idealPhase = "test";
1820            }
1821            
1822            // If the ideal phase is missing, add it
1823            if (idealPhase != null && !relevantPhases.contains(idealPhase)) {
1824                neededPhases.add(idealPhase);
1825            }
1826        }
1827        
1828        // Add missing phases and reorganize goals
1829        for (String phase : neededPhases) {
1830            relevantPhases.add(phase);
1831            goalsByPhase.put(phase, new ArrayList<>());
1832            System.err.println("DEBUG: Added missing phase: " + phase);
1833        }
1834        
1835        // Re-organize goals now that we have the right phases
1836        if (!neededPhases.isEmpty()) {
1837            // Clear existing assignments for re-assignment
1838            for (List<String> goals : goalsByPhase.values()) {
1839                goals.clear();
1840            }
1841            
1842            // Re-assign goals with proper phases available
1843            for (Map<String, Object> goalInfo : pluginGoals) {
1844                String targetName = (String) goalInfo.get("targetName");
1845                String goal = (String) goalInfo.get("goal");
1846                String targetType = (String) goalInfo.get("targetType");
1847                
1848                if (targetName == null) continue;
1849                
1850                String assignedPhase = null;
1851                if ("build".equals(targetType)) {
1852                    if (goal.contains("testCompile") || goal.equals("testCompile")) {
1853                        assignedPhase = "test-compile";
1854                    } else if (goal.contains("compile") && !goal.contains("test")) {
1855                        assignedPhase = "compile";
1856                    } else {
1857                        assignedPhase = "package";
1858                    }
1859                } else if ("test".equals(targetType)) {
1860                    assignedPhase = "test";
1861                } else if ("serve".equals(targetType)) {
1862                    assignedPhase = "compile";
1863                } else {
1864                    assignedPhase = "validate";
1865                }
1866                
1867                // Assign to phase if it exists
1868                if (assignedPhase != null && relevantPhases.contains(assignedPhase)) {
1869                    goalsByPhase.get(assignedPhase).add(targetName);
1870                } else {
1871                    // Fallback logic
1872                    String fallbackPhase = findBestFallbackPhase(assignedPhase, relevantPhases);
1873                    if (fallbackPhase != null) {
1874                        goalsByPhase.get(fallbackPhase).add(targetName);
1875                    }
1876                }
1877            }
1878        }
1879    }
1880    
1881    /**
1882     * Find the best fallback phase when the ideal phase doesn't exist
1883     */
1884    private static String findBestFallbackPhase(String idealPhase, List<String> availablePhases) {
1885        // Define fallback mappings for common cases
1886        Map<String, String[]> fallbackMap = new LinkedHashMap<>();
1887        fallbackMap.put("test-compile", new String[]{"compile", "validate"});
1888        fallbackMap.put("test", new String[]{"compile", "validate"});
1889        fallbackMap.put("package", new String[]{"compile", "validate"});
1890        fallbackMap.put("verify", new String[]{"package", "compile", "validate"});
1891        fallbackMap.put("install", new String[]{"package", "compile", "validate"});
1892        fallbackMap.put("deploy", new String[]{"install", "package", "compile", "validate"});
1893        fallbackMap.put("integration-test", new String[]{"package", "compile", "validate"});
1894        
1895        // Try fallback options in order
1896        String[] fallbacks = fallbackMap.get(idealPhase);
1897        if (fallbacks != null) {
1898            for (String fallback : fallbacks) {
1899                if (availablePhases.contains(fallback)) {
1900                    return fallback;
1901                }
1902            }
1903        }
1904        
1905        // If no specific fallback found, use general fallbacks based on phase type
1906        if (idealPhase.contains("test")) {
1907            // Test-related phases fall back to compile if available
1908            if (availablePhases.contains("compile")) {
1909                return "compile";
1910            }
1911        } else if (idealPhase.contains("compile")) {
1912            // Compile phases fall back to validate
1913            if (availablePhases.contains("validate")) {
1914                return "validate";
1915            }
1916        }
1917        
1918        // Last resort: use validate if available, otherwise null (skip goal)
1919        return availablePhases.contains("validate") ? "validate" : null;
1920    }
1921    
1922    /**
1923     * Generate goal-to-goal dependencies where goals depend on goals from prerequisite phases
1924     */
1925    private static Map<String, List<String>> generateGoalDependencies(
1926            List<Map<String, Object>> pluginGoals,
1927            Map<String, List<String>> goalsByPhase,
1928            Map<String, List<String>> phaseDependencies,
1929            List<String> relevantPhases) {
1930        
1931        Map<String, List<String>> goalDependencies = new LinkedHashMap<>();
1932        
1933        // For each goal, find which phase it belongs to and make it depend on goals from prerequisite phases
1934        for (Map<String, Object> goalInfo : pluginGoals) {
1935            String targetName = (String) goalInfo.get("targetName");
1936            if (targetName == null) continue;
1937            
1938            // Find which phase this goal belongs to
1939            String goalPhase = null;
1940            for (Map.Entry<String, List<String>> entry : goalsByPhase.entrySet()) {
1941                if (entry.getValue().contains(targetName)) {
1942                    goalPhase = entry.getKey();
1943                    break;
1944                }
1945            }
1946            
1947            if (goalPhase != null) {
1948                List<String> prerequisiteGoals = new ArrayList<>();
1949                
1950                // Traverse the full phase dependency chain to find phases with actual goals
1951                Set<String> visitedPhases = new LinkedHashSet<>();
1952                findPrerequisiteGoalsRecursively(goalPhase, phaseDependencies, goalsByPhase, 
1953                                                relevantPhases, prerequisiteGoals, visitedPhases);
1954                
1955                // Remove duplicates and add to dependencies
1956                if (!prerequisiteGoals.isEmpty()) {
1957                    Set<String> uniqueGoals = new LinkedHashSet<>(prerequisiteGoals);
1958                    goalDependencies.put(targetName, new ArrayList<>(uniqueGoals));
1959                }
1960            }
1961        }
1962        return goalDependencies;
1963    }
1964    
1965    /**
1966     * Recursively find prerequisite goals by traversing the phase dependency chain
1967     */
1968    private static void findPrerequisiteGoalsRecursively(
1969            String phase,
1970            Map<String, List<String>> phaseDependencies,
1971            Map<String, List<String>> goalsByPhase,
1972            List<String> relevantPhases,
1973            List<String> prerequisiteGoals,
1974            Set<String> visitedPhases) {
1975        
1976        // Avoid infinite loops
1977        if (visitedPhases.contains(phase)) {
1978            return;
1979        }
1980        visitedPhases.add(phase);
1981        
1982        // Get immediate prerequisite phases
1983        List<String> immediatePrereqs = phaseDependencies.get(phase);
1984        
1985        if (immediatePrereqs != null) {
1986            for (String prereqPhase : immediatePrereqs) {
1987                // Check if this prerequisite phase has goals (regardless of whether it's in relevantPhases)
1988                List<String> goalsInPhase = goalsByPhase.get(prereqPhase);
1989                
1990                if (goalsInPhase != null && !goalsInPhase.isEmpty()) {
1991                    // Found goals in this phase - add them
1992                    prerequisiteGoals.addAll(goalsInPhase);
1993                } else {
1994                    // No goals in this phase - continue traversing (even if not in relevantPhases)
1995                    findPrerequisiteGoalsRecursively(prereqPhase, phaseDependencies, goalsByPhase,
1996                                                   relevantPhases, prerequisiteGoals, visitedPhases);
1997                }
1998            }
1999        }
2000    }
2001}

