 500// Plugin discovery interfaces
 501interface PluginGoal {
 502    goal: string;
 503    description: string;
 504    phase?: string;
 505}
 506
 507interface DiscoveredPlugin {
 508    groupId: string;
 509    artifactId: string;
 510    version: string;
 511    goals: PluginGoal[];
 512}
 513
 514// Cache for plugin discovery results
 515const pluginGoalsCache = new Map<string, DiscoveredPlugin>();
 516
 517// Process pool for controlled concurrent plugin discovery
 518class PluginDiscoveryPool {
 519    private activeRequests = 0;
 520    private readonly maxConcurrent = 6; // Increased to 6 concurrent processes for better performance
 521    private readonly queue: Array<{ pluginKey: string; projectRoot: string; resolve: (result: DiscoveredPlugin | null) => void }> = [];
 522    private mavenCommand: string | null = null;
 523    private activeProcesses = new Set<ChildProcess>(); // Track active child processes for cleanup
 524
 525    async discover(pluginKey: string, projectRoot: string): Promise<DiscoveredPlugin | null> {
 526        // Check cache first
 527        if (pluginGoalsCache.has(pluginKey)) {
 528            console.log(`[Maven Plugin Discovery] Using cached result for ${pluginKey}`);
 529            return pluginGoalsCache.get(pluginKey)!;
 530        }
 531
 532        // Initialize maven command on first use
 533        if (this.mavenCommand === null) {
 534            await this.initializeMavenCommand();
 535        }
 536
 537        console.log(`[Maven Plugin Discovery] Queueing discovery request for ${pluginKey}`);
 538        return new Promise((resolve) => {
 539            this.queue.push({ pluginKey, projectRoot, resolve });
 540            this.processQueue();
 541        });
 542    }
 543
 544    private async initializeMavenCommand(): Promise<void> {
 545        try {
 546            const { exec } = await import('child_process');
 547            const { promisify } = await import('util');
 548            const execAsync = promisify(exec);
 549
 550            await execAsync('which mvnd', { timeout: 1000 });
 551            this.mavenCommand = 'mvnd help:describe';
 552            console.log(`[Maven Plugin Discovery] Maven Daemon (mvnd) detected - will use for faster execution`);
 553        } catch {
 554            this.mavenCommand = 'mvn help:describe';
 555            console.log(`[Maven Plugin Discovery] Using standard Maven (mvn) - consider installing Maven Daemon for faster execution`);
 556        }
 557    }
 558
 559    private async processQueue() {
 560        if (this.activeRequests >= this.maxConcurrent || this.queue.length === 0) {
 561            return;
 562        }
 563
 564        const request = this.queue.shift()!;
 565        this.activeRequests++;
 566
 567        console.log(`[Maven Plugin Discovery] Starting discovery for ${request.pluginKey} (${this.activeRequests}/${this.maxConcurrent} active, ${this.queue.length} queued)`);
 568
 569        try {
 570            const result = await this.executeDiscovery(request.pluginKey, request.projectRoot);
 571            if (result) {
 572                console.log(`[Maven Plugin Discovery] Successfully discovered ${result.goals.length} goals for ${request.pluginKey}`);
 573            } else {
 574                console.log(`[Maven Plugin Discovery] No goals found for ${request.pluginKey}`);
 575            }
 576            request.resolve(result);
 577        } catch (error) {
 578            console.warn(`[Maven Plugin Discovery] Failed to discover goals for ${request.pluginKey}:`, error.message);
 579            request.resolve(null);
 580        } finally {
 581            this.activeRequests--;
 582            console.log(`[Maven Plugin Discovery] Completed discovery for ${request.pluginKey} (${this.activeRequests}/${this.maxConcurrent} active, ${this.queue.length} queued)`);
 583            // Process next item in queue immediately for better performance
 584            setImmediate(() => this.processQueue());
 585        }
 586    }
 587
 588    private async executeDiscovery(pluginKey: string, projectRoot: string): Promise<DiscoveredPlugin | null> {
 589        let childProcess: ChildProcess | null = null;
 590        try {
 591            console.log(`[Maven Plugin Discovery] Executing plugin discovery for ${pluginKey} in ${projectRoot}`);
 592            const startTime = Date.now();
 593
 594            const { spawn } = await import('child_process');
 595
 596            // Use spawn instead of exec for better process control
 597            const args = [
 598                'help:describe',
 599                `-Dplugin=${pluginKey}`,
 600                '-Ddetail=false',
 601                '-q',
 602                '-o',
 603                '-B',
 604                '--no-transfer-progress',
 605                `-Dmaven.repo.local=${process.env.HOME}/.m2/repository`
 606            ];
 607
 608            const command = this.mavenCommand!.split(' ')[0]; // 'mvn' or 'mvnd'
 609
 610            childProcess = spawn(command, args, {
 611                cwd: projectRoot,
 612                stdio: ['ignore', 'pipe', 'pipe'],
 613                env: {
 614                    ...process.env,
 615                    'MAVEN_OPTS': '-Xmx128m -XX:+UseParallelGC -XX:+TieredCompilation -XX:TieredStopAtLevel=1 -Djava.awt.headless=true -Dfile.encoding=UTF-8'
 616                }
 617            });
 618
 619            // Track the process for cleanup
 620            this.activeProcesses.add(childProcess);
 621
 622            return new Promise((resolve) => {
 623                let stdout = '';
 624                let stderr = '';
 625                let isResolved = false;
 626
 627                const resolveOnce = (result: DiscoveredPlugin | null) => {
 628                    if (!isResolved) {
 629                        isResolved = true;
 630                        if (childProcess) {
 631                            this.activeProcesses.delete(childProcess);
 632                        }
 633                        resolve(result);
 634                    }
 635                };
 636
 637                if (childProcess?.stdout) {
 638                    childProcess.stdout.on('data', (data: Buffer) => {
 639                        stdout += data.toString();
 640                    });
 641                }
 642
 643                if (childProcess?.stderr) {
 644                    childProcess.stderr.on('data', (data: Buffer) => {
 645                        stderr += data.toString();
 646                    });
 647                }
 648
 649                childProcess?.on('close', (code: number | null) => {
 650                    const execTime = Date.now() - startTime;
 651
 652                    if (code === 0) {
 653                        console.log(`[Maven Plugin Discovery] Maven command completed for ${pluginKey} in ${execTime}ms`);
 654
 655                        if (stdout.trim()) {
 656                            console.log(`[Maven Plugin Discovery] Raw stdout for ${pluginKey}:`, JSON.stringify(stdout.substring(0, 500)));
 657
 658                            const goals = parsePluginDescribeOutput(stdout);
 659                            console.log(`[Maven Plugin Discovery] Parsed ${goals.length} goals from output for ${pluginKey}:`, goals.map(g => g.goal).join(', '));
 660
 661                            if (goals.length > 0) {
 662                                const [groupId, artifactId] = pluginKey.split(':');
 663
 664                                const discoveredPlugin: DiscoveredPlugin = {
 665                                    groupId,
 666                                    artifactId,
 667                                    version: 'unknown',
 668                                    goals
 669                                };
 670
 671                                pluginGoalsCache.set(pluginKey, discoveredPlugin);
 672                                console.log(`[Maven Plugin Discovery] Cached discovery result for ${pluginKey}`);
 673                                resolveOnce(discoveredPlugin);
 674                                return;
 675                            }
 676                        }
 677                    } else {
 678                        console.error(`[Maven Plugin Discovery] Process exited with code ${code} for ${pluginKey}. stderr:`, stderr);
 679                    }
 680
 681                    resolveOnce(null);
 682                });
 683
 684                childProcess?.on('error', (error: Error) => {
 685                    console.error(`[Maven Plugin Discovery] Process error for ${pluginKey}:`, error.message);
 686                    resolveOnce(null);
 687                });
 688
 689                // Set timeout
 690                const timeoutId = setTimeout(() => {
 691                    if (!isResolved && childProcess && this.activeProcesses.has(childProcess)) {
 692                        console.warn(`[Maven Plugin Discovery] Timeout for ${pluginKey}, killing process`);
 693                        try {
 694                            childProcess.kill('SIGTERM');
 695                            setTimeout(() => {
 696                                if (childProcess && childProcess.pid && !childProcess.killed) {
 697                                    childProcess.kill('SIGKILL');
 698                                }
 699                            }, 2000);
 700                        } catch (error: any) {
 701                            console.warn(`[Maven Plugin Discovery] Error killing timed out process:`, error?.message);
 702                        }
 703                        resolveOnce(null);
 704                    }
 705                }, 15000); // Increased timeout to 15 seconds
 706
 707                // Clear timeout if process completes
 708                childProcess?.on('close', () => clearTimeout(timeoutId));
 709            });
 710
 711        } catch (error: any) {
 712            if (childProcess && this.activeProcesses.has(childProcess)) {
 713                try {
 714                    childProcess.kill('SIGTERM');
 715                } catch (killError: any) {
 716                    console.warn(`[Maven Plugin Discovery] Error killing process:`, killError?.message);
 717                }
 718                this.activeProcesses.delete(childProcess);
 719            }
 720            console.error(`[Maven Plugin Discovery] Error during discovery for ${pluginKey}:`, error?.message || error, error?.code || '');
 721            return null;
 722        }
 723    }
 724
 725    cleanup(): void {
 726        console.log(`[Maven Plugin Discovery] Cleaning up ${this.activeProcesses.size} active processes`);
 727        for (const process of this.activeProcesses) {
 728            try {
 729                if (process && process.pid && !process.killed) {
 730                    console.log(`[Maven Plugin Discovery] Killing process ${process.pid}`);
 731                    process.kill('SIGTERM');
 732                    setTimeout(() => {
 733                        if (process.pid && !process.killed) {
 734                            console.log(`[Maven Plugin Discovery] Force killing process ${process.pid}`);
 735                            process.kill('SIGKILL');
 736                        }
 737                    }, 2000);
 738                }
 739            } catch (error: any) {
 740                console.warn(`[Maven Plugin Discovery] Error cleaning up process:`, error?.message || error);
 741            }
 742        }
 743        this.activeProcesses.clear();
 744    }
 745}
